INSERT INTO `bookstore`.`blog` (`id`,`name`,`picture`,`content`,`dateCreate`,`comment`,`shortcut`,`user_id`) VALUES (1, 'Đại dương đen: Ánh sáng giữa đại dương tăm tối\r\n', 'https://reviewsach.net/wp-content/uploads/2022/03/cover-sach-dai-duong-den-324x235.jpeg\r\n', '\r\n<div class=\"td-ss-main-content\">\r\n                                    \r\n        \r\n\r\n        <div class=\"td-post-content tagdiv-type\">\r\n            \r\n            <p style=\"text-align: justify;\"><span style=\"font-weight: 400;\">Tiếp nối thành công từ những cuốn sách trước đó như </span><span style=\"font-weight: 400;\">“<a href=\"https://reviewsach.net/buc-xuc-khong-lam-ta-vo-can/\" data-wpel-link=\"internal\" rel=\"nofollow noopener noreferrer\">Bức xúc không làm ta vô can</a>”, “<a href=\"https://reviewsach.net/thien-ac-va-smartphone/\" data-wpel-link=\"internal\" rel=\"nofollow noopener noreferrer\">Thiện – ác và smartphone</a>”, “<a href=\"https://reviewsach.net/tim-minh-trong-the-gioi-hau-tuoi-tho/\" data-wpel-link=\"internal\" rel=\"nofollow noopener noreferrer\">Tìm mình trong thế giới hậu tuổi thơ</a>”</span><span style=\"font-weight: 400;\">, Tiến sĩ Đặng Hoàng Giang công bố cuốn sách mang tên </span><span style=\"font-weight: 400;\">“Đại dương đen”</span><span style=\"font-weight: 400;\"> – kết tinh hành trình đầy nhọc nhằn và nhẫn nại của tác giả với người trầm cảm trong suốt hai năm ròng. Cuốn sách ghi lại tiếng nói đầy xót xa, ám ảnh của những người đang ngấp ngoải trong “đại dương” trầm cảm, đồng thời đem đến cho người đọc góc nhìn đầy khoa học khi cung cấp những kiến thức cơ bản về trầm cảm từ nguyên nhân, hậu quả, phương thức trị liệu, đến cách đối mặt với trầm cảm.&nbsp;</span></p>\r\n<div id=\"at-widget\">\r\n        <style type=\"text/css\">\r\n            .cps-wrap {\r\n                width: 100%;\r\n                margin: 0;\r\n                padding: 0;\r\n                border: 0;\r\n                font: inherit;\r\n                font-size: 100%;\r\n                vertical-align: baseline;\r\n                display: flex;\r\n                flex-wrap: wrap;\r\n            }\r\n\r\n            .cps-widget {\r\n                width: calc(95% / 3);\r\n                position: relative;\r\n                font-family: Open Sans, Helvetica, Arial, sans-serif;\r\n                background: #fff;\r\n                opacity: 1;\r\n                transition: opacity .2s ease-in-out;\r\n                float: left;\r\n                font-size: 14.1px;\r\n                line-height: 21.15px;\r\n                padding: 6px;\r\n            }\r\n\r\n            .cps-widget-border {\r\n                border: 1px solid #d6d6d6;\r\n            }\r\n\r\n            .cps-widget:hover .cps-widget-product-buynow {\r\n                display: inline-block;\r\n            }\r\n\r\n            .cps-widget-img {\r\n                width: 100%;\r\n                position: relative;\r\n            }\r\n\r\n            .cps-widget-img:after {\r\n                content: \"\";\r\n                display: block;\r\n                padding-bottom: 100%;\r\n            }\r\n\r\n            .cps-widget-img img {\r\n                position: absolute;\r\n                top: 0;\r\n                bottom: 0;\r\n                left: 0;\r\n                right: 0;\r\n                width: 100%;\r\n                height: 100%;\r\n                object-fit: cover;\r\n                object-position: center;\r\n            }\r\n\r\n            .cps-widget-product-name {\r\n                text-align: left;\r\n                margin-top: 5px;\r\n                padding: 0 4px;\r\n                height: 42px;\r\n                overflow: hidden;\r\n                text-overflow: ellipsis;\r\n                word-wrap: break-word;\r\n                -webkit-box-orient: vertical;\r\n                display: -webkit-box;\r\n                -webkit-line-clamp: 2;\r\n            }\r\n\r\n            .cps-widget-product-name a {\r\n                text-decoration: none;\r\n                color: #000 !important\r\n            }\r\n\r\n            .cps-widget-product-price {\r\n                text-align: left;\r\n                font-weight: bold;\r\n                height: 40px;\r\n                padding: 0 4px 4px;\r\n                overflow: hidden;\r\n                color: #4589ca;\r\n            }\r\n\r\n            .cps-widget-product-buynow-p {\r\n                position: absolute;\r\n                text-align: center;\r\n                left: 0;\r\n                right: 0;\r\n                top: 50%;\r\n            }\r\n\r\n            .cps-widget-product-buynow {\r\n                display: none;\r\n                padding: 2px 10px;\r\n                line-height: 29.375px;\r\n                background: #4589ca;\r\n            }\r\n\r\n            .cps-widget-product-buynow a {\r\n                text-decoration: none;\r\n                color: #fff !important;\r\n                display: block;\r\n            }\r\n\r\n            .cps-widget-product-buynow a:hover {\r\n                color: #fff !important\r\n            }\r\n\r\n            .cps-widget-product-discount-table {\r\n                position: absolute;\r\n                top: 0;\r\n                right: 0;\r\n                font-size: 13px;\r\n                color: #fff !important;\r\n                background: #4589ca;\r\n                padding: 0 5px;\r\n                border-radius: 5px;\r\n            }\r\n\r\n            .at-cps-row-container {\r\n                border-bottom: 1px dotted #ccc;\r\n                padding: 10px 0;\r\n            }\r\n\r\n            .at-cps-grid-container {\r\n                display: -ms-grid;\r\n                display: grid;\r\n                padding: 5px 10px 0;\r\n                width: 100%;\r\n                box-sizing: border-box;\r\n                position: relative;\r\n                grid-template-columns: 100px repeat(2, 1fr);\r\n            }\r\n\r\n           