: center;\r\n                justify-content: center;\r\n                -ms-flex-direction: column;\r\n                flex-direction: column;\r\n            }\r\n\r\n            .at-cps-aff-button {\r\n                width: 110px;\r\n                height: 40px;\r\n                -ms-flex: 1;\r\n                flex: 1;\r\n                color: #ffffff !important;\r\n                padding: 8px 10px;\r\n                text-decoration: none;\r\n                font-size: 13px;\r\n                font-weight: 700;\r\n                background-color: #4589ca !important;\r\n                word-break: normal;\r\n                letter-spacing: normal;\r\n                border-radius: 0px;\r\n                border: none;\r\n                /*-webkit-box-shadow: 0px 8px 15px rgba(0, 0, 0, 0.2);\r\n                -moz-box-shadow: 0px 8px 15px rgba(0, 0, 0, 0.2);\r\n                box-shadow: 0px 8px 15px rgba(0, 0, 0, 0.2);*/\r\n                transition: all 0.3s ease 0s;\r\n                text-align: center;\r\n            }\r\n\r\n            .at-cps-aff-button:hover {\r\n                background-image: linear-gradient(rgba(0, 0, 0, 0.2), rgba(0, 0, 0, 0.2));\r\n                color: #ffffff;\r\n            }\r\n\r\n            .at-cps-image-container {\r\n                display: -ms-flexbox;\r\n                display: flex;\r\n                -ms-flex-align: center;\r\n                align-items: center;\r\n                -ms-flex-pack: center;\r\n                justify-content: center;\r\n                height: 75px;\r\n            }\r\n\r\n            .at-cps-text {\r\n                color: #333;\r\n                text-align: left;\r\n            }\r\n\r\n            .at-cps-text span {\r\n                color: #888;\r\n                font-weight: 400;\r\n            }\r\n\r\n            .at-sps-text-line {\r\n                color: #848484;\r\n                text-decoration: line-through;\r\n                font-size: 13px;\r\n                font-weight: 400;\r\n            }\r\n\r\n            .at-cps-align-item {\r\n                align-items: flex-start;\r\n                padding-left: 5px !important;\r\n            }\r\n\r\n            .at-cps-aff-button-main a:active,\r\n            .at-cps-aff-button-main a:focus {\r\n                color: #ffffff;\r\n            }\r\n\r\n            .at-cps-relative {\r\n                position: relative;\r\n            }\r\n\r\n            .at-cps-sup {\r\n                position: relative;\r\n                font-size: 75%;\r\n                line-height: 0;\r\n                vertical-align: baseline;\r\n                top: -5px;\r\n            }\r\n\r\n            .at-cps-btn {\r\n                display: inline-block;\r\n                color: #fff !important;\r\n                text-decoration: none;\r\n                font-size: 13px;\r\n                font-weight: 700;\r\n                background-color: #4589ca !important;\r\n                word-break: normal;\r\n                letter-spacing: normal;\r\n                border-radius: 0px;\r\n                border: none;\r\n                transition: all 0.3s ease 0s;\r\n                text-align: center;\r\n                padding: 7px 14px;\r\n                margin-bottom: 0;\r\n                white-space: nowrap;\r\n                vertical-align: middle;\r\n            }\r\n\r\n            .at-cps-btn:hover {\r\n                background-image: linear-gradient(rgba(0, 0, 0, 0.2), rgba(0, 0, 0, 0.2));\r\n                color: #ffffff;\r\n            }\r\n\r\n            @media  screen and (max-width: 767px) {\r\n                .cps-widget {\r\n                    width: 50%;\r\n                }\r\n            }\r\n            /*type 3 ---------------------------------------------------------------------*/\r\n        .at-cps-3{\r\n        display: flex;\r\n    }\r\n    .at-cps-3>a{\r\n        flex: 0 0 35%;\r\n        margin-right: 10px;\r\n    }\r\n    .at-cps-3>a>img{\r\n        display: block;\r\n        width: auto;\r\n        height: auto !important;\r\n        margin: auto;\r\n        max-width: 100%;\r\n        max-height: 200px;\r\n    }\r\n    .at-cps-3>div{\r\n        flex: 1;\r\n    }\r\n    .at-cps-3-item{\r\n        display: flex;\r\n        margin-bottom: 15px;\r\n        align-items: center;\r\n    }\r\n    .at-cps-3-flex-1{\r\n        flex: 1;\r\n    }\r\n    .at-cps-3-item .at-price {\r\n        font-weight: 600;\r\n        font-size: 16px;\r\n        color: #00695C;\r\n    }\r\n    .at-cps-3-title{\r\n        margin-bottom: 10px;\r\n        font-weight: 500;\r\n        letter-spacing: -0.02em;\r\n        line-height: 1.2;\r\n        -webkit-font-smoothing: antialiased;\r\n        -moz-osx-font-smoothing: grayscale;\r\n        font-size: 18px;\r\n        overflow: hidden;\r\n        display: -webkit-box;\r\n        text-overflow: ellipsis;\r\n        -webkit-box-orient: vertical;\r\n        -webkit-line-clamp: 3;\r\n    }\r\n    .at-cps-3-shop{\r\n        font-weight: 500;\r\n        color: #4589ca !important;\r\n        text-decoration: none;\r\n    }\r\n    .at-cps-3-flex-1:nth-child(3){\r\n        text-align: right;\r\n    }\r\n    @media  screen and (max-width: 767px){\r\n        .at-cps-3 {\r\n            display: block;\r\n            padding: 0 10px;\r\n        }\r\n        .at-cps-3-title{\r\n            text-align: center;\r\n            margin-top: 10px;\r\n        }\r\n        .at-cps-3>a{\r\n            margin-right: 0;\r\n        }\r\n    }\r\n\r\n            </style> <div class=\"cps-wrap\"> <div class=\"at-cps-3\">\r\n    <a target=\"_blank\" href=\"https://shorten.asia/jjC6t7GU\" rel=\"nofollow\">\r\n        <img src=\"https://salt.tikicdn.com/cache/280x280/ts/product/f7/fb/9a/e8b9a94478dc887c4b84b6b6439f6335.jpg\" alt=\"Đại Dương Đen - Những Câu Chuyện Từ Thế Giới Của Trầm Cảm\">\r\n    </a>\r\n    <div>\r\n        <p class=\"at-cps-3-title\">Đại Dương Đen - Những Câu Chuyện Từ Thế Giới Của Trầm Cảm</p>\r\n        <div class=\"at-cps-3-item\">\r\n            <div class=\"at-cps-3-flex-1\">\r\n                <a class=\"at-cps-3-shop\" rel=\"nofollow\" target=\"_blank\" href=\" https://shorten.asia/jjC6t7GU\">  tiki </a>\r\n            </div>\r\n            <div class=\"at-cps-3-flex-1\">\r\n                <div class=\"mo-price-row\">\r\n                    <div class=\"mo-price\">171,500<span class=\"mo-cps-sup\">đ</span></div>\r\n                </div>\r\n            </div>\r\n            <div class=\"at-cps-3-flex-1\">\r\n                <a target=\"_blank\" href=\"  https://shorten.asia/jjC6t7GU \" class=\"at-cps-btn\" rel=\"nofollow\"><span> Mua Ngay </span></a>\r\n            </div>\r\n        </div><div class=\"at-cps-3-item\">\r\n            <div class=\"at-cps-3-flex-1\">\r\n                <a class=\"at-cps-3-shop\" rel=\"nofollow\" target=\"_blank\" href=\" https://shorten.asia/upHXSeh5\">  shopee </a>\r\n            </div>\r\n            <div class=\"at-cps-3-flex-1\">\r\n                <div class=\"mo-price-row\">\r\n                    <div class=\"mo-price\">180,000.0<span class=\"mo-cps-sup\">đ</span></div>\r\n                </div>\r\n            </div>\r\n            <div class=\"at-cps-3-flex-1\">\r\n                <a target=\"_blank\" href=\"  https://shorten.asia/upHXSeh5 \" class=\"at-cps-btn\" rel=\"nofollow\"><span> Mua Ngay </span></a>\r\n            </div>\r\n        </div>\r\n    </div>\r\n</div> </div></div>\r\n<p><script data-minify=\"1\" async=\"\" type=\"application/javascript\" src=\"https://reviewsach.net/wp-content/cache/min/1/pub/files/e91f90bb9e1d28808d5a7c14823af132.js?ver=1653448505\"></script></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400;\">Sách được chia làm 2 phần. Phần đầu là 12 câu chuyện của những nhân vật có thật cùng những chứng bệnh: trầm cảm, rối loạn lưỡng cực, rối loạn lo âu… Phần thứ 2 cung cấp kiến thức, phương pháp trị liệu và những hiểu biết về trầm cảm mà ai cũng nên biết.&nbsp;</span></p>\r\n<figure id=\"attachment_8795\" aria-describedby=\"caption-attachment-8795\" style=\"width: 480px\" class=\"wp-caption aligncenter\"><img class=\"size-full wp-image-8795 entered lazyloaded\" src=\"https://reviewsach.net/wp-content/uploads/2022/03/dai-duong-den-dang-hoang-giang.jpeg\" alt=\"dai duong den dang hoang giang\" width=\"480\" height=\"600\" data-lazy-srcset=\"https://reviewsach.net/wp-content/uploads/2022/03/dai-duong-den-dang-hoang-giang.jpeg 480w, https://reviewsach.net/wp-content/uploads/2022/03/dai-duong-den-dang-hoang-giang-150x188.jpeg 150w, https://reviewsach.net/wp-content/uploads/2022/03/dai-duong-den-dang-hoang-giang-300x375.jpeg 300w, https://reviewsach.net/wp-content/uploads/2022/03/dai-duong-den-dang-hoang-giang-336x420.jpeg 336w\" data-lazy-sizes=\"(max-width: 480px) 100vw, 480px\" data-lazy-src=\"https://reviewsach.net/wp-content/uploads/2022/03/dai-duong-den-dang-hoang-giang.jpeg\" data-ll-status=\"loaded\" sizes=\"(max-width: 480px) 100vw, 480px\" srcset=\"https://reviewsach.net/wp-content/uploads/2022/03/dai-duong-den-dang-hoang-giang.jpeg 480w, https://reviewsach.net/wp-content/uploads/2022/03/dai-duong-den-dang-hoang-giang-150x188.jpeg 150w, https://reviewsach.net/wp-content/uploads/2022/03/dai-duong-den-dang-hoang-giang-300x375.jpeg 300w, https://reviewsach.net/wp-content/uploads/2022/03/dai-duong-den-dang-hoang-giang-336x420.jpeg 336w\"><noscript><img class=\"size-full wp-image-8795\" src=\"https://reviewsach.net/wp-content/uploads/2022/03/dai-duong-den-dang-hoang-giang.jpeg\" alt=\"dai duong den dang hoang giang\" width=\"480\" height=\"600\" srcset=\"https://reviewsach.net/wp-content/uploads/2022/03/dai-duong-den-dang-hoang-giang.jpeg 480w, https://reviewsach.net/wp-content/uploads/2022/03/dai-duong-den-dang-hoang-giang-150x188.jpeg 150w, https://reviewsach.net/wp-content/uploads/2022/03/dai-duong-den-dang-hoang-giang-300x375.jpeg 300w, https://reviewsach.net/wp-content/uploads/2022/03/dai-duong-den-dang-hoang-giang-336x420.jpeg 336w\" sizes=\"(max-width: 480px) 100vw, 480px\" /></noscript><figcaption id=\"caption-attachment-8795\" class=\"wp-caption-text\">Ảnh: Instagram @cuonghoangreading</figcaption></figure>\r\n<h2 style=\"text-align: justify;\"><strong>Dữ dội, loang lổ nhưng vỗ về và nhân văn</strong></h2>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400;\">Cuốn sách trước hết ghi lại những trải nghiệm thực tế của những người mắc bệnh trầm cảm, cách họ sống và làm việc, yêu đương và cống hiến với căn bệnh quái ác nhưng thiếu sự hiểu biết và đồng cảm từ người thân, gia đình, xã hội. Nhân vật chính trong những câu chuyện không bó hẹp với bất cứ ai ở bất kì giới tính, độ tuổi; nghề nghiệp hay địa vị xã hội.&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400;\">Như tác giả Đặng Hoàng Giang đã tóm tắt: </span><i><span style=\"font-weight: 400;\">“Nó không chỉ có ở trong giới trẻ, “vì chúng vốn thất thường trong cảm xúc.” Không chỉ ở trong giới văn nghệ sĩ, “vì họ quá nhạy cảm”. Không chỉ ở người có kinh tế đầy đủ, “bởi người nghèo lo kiếm sống thì lấy đâu ra thời gian mà trầm cảm”. Trầm cảm phổ biến như thế nào? Nếu bạn có 1000 người bạn Facebook, thì trong năm qua, bảy mươi người trong số đó mắc trầm cảm.”</span></i></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400;\">Từng câu từng chữ như đan bện vào nhau tạo ra bức màn tăm tối ngăn cách những bệnh nhân trầm cảm được sống, được cống hiến, được yêu thương và hạnh phúc. Những câu chuyện u ám, ảm đạm, ngạt thở, dữ dội, không lối thoát có thể sẽ gây kích động tâm lí cho người đọc:&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400;\">Ta được gặp Uyên, 21 tuổi, sinh viên ngành Kinh tế và cách đối chọi với căn bệnh đầy cực đoan: tự làm đau cơ thể. Ham muốn làm đau bản thân dữ dội và thường trực, Uyên luôn thuyết phục bạn bè “đưa cho mình vật gì nhọn để tự hại”. Chỉ khi đó, Uyên mới có thể “</span><i><span style=\"font-weight: 400;\">trở lại bình thường và sự căng thẳng dịu xuống”.</span></i></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400;\">Hay người mẹ mới sinh bị cô lập trong sự ghẻ lạnh, thờ ơ của cả gia đình bố mẹ đẻ và gia đình nhà chồng đến độ có những suy nghĩ làm hại chính đứa con mình sinh ra: </span><i><span style=\"font-weight: 400;\">“Tôi biết là mình đang bị trầm cảm. Đây không phải là lần đầu tôi muốn làm cho H đau. Tôi yêu nó vô cùng, tôi đã suýt mất mạng khi sinh nó ra, nhưng tôi vẫn có cảm giác muốn đày đọa nó.”</span></i></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400;\">Căn bệnh trầm cảm dường như cũng “lây lan” như dịch bệnh, căn bệnh mà mầm mống của nó dường như không thể bị dập tắt. Đời ông/bà, đời cha/mẹ, đời con, đời cháu,… cứ thế duy trì những bất ổn trong tinh thần. Rồi từ một thành viên trong gia đình, trầm cảm hủy hoại cuộc sống của tất cả những người xung quanh. Tựa như một giọt dầu loang trên mặt nước, trầm cảm cứ thế nuốt chửng lấy những ánh sáng thoi thóp của ngày tàn:&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400;\">Thành, 29 tuổi, là một nhân viên văn phòng, anh chống chọi với căn bệnh trầm cảm trong nhiều năm. Có lẽ nhiều người không thể hiểu được cảm giác mà Thành và gia đình phải chịu đựng.</span> <span style=\"font-weight: 400;\">Khi đi tìm nguồn cơn của căn bệnh, Thành nhận ra căn bệnh đang giày vò anh hàng ngày hàng giờ đã từng giày vò ông, cha mình.&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400;\">Thành nhớ về những gì được biết về người ông. </span><span style=\"font-weight: 400;\">Bố Thành như bản sao của ông nội và Thành cũng đau đớn nhận ra, anh là người lưu giữ “vật chất di truyền” đầy trái ngang ấy. Không chỉ ông nội và cha, Thành cũng có một người mẹ chịu nhiều thương tổn, chỉ khác là mẹ của Thành có lẽ chưa phải một người trầm cảm hay có các vấn đề về tâm thần khác.</span></p>\r\n<p style=\"text-align: justify;\"><i><span style=\"font-weight: 400;\">“Mẹ anh lớn lên với nhiều đòn roi từ bà ngoại mà lại ít sự chỉ bảo, và trở thành một kẻ phụ thuộc và nhu nhược. Đến khi đi lấy chồng, mẹ anh vẫn không phân biệt được các loại thịt. Cái điều khiển ti vi mẹ cũng chỉ biết duy nhất cái nút tắt bật và tiến lùi, và tới giờ bà vẫn không biết đi xe máy.</span></i></p>\r\n<figure id=\"attachment_8796\" aria-describedby=\"caption-attachment-8796\" style=\"width: 529px\" class=\"wp-caption aligncenter\"><img class=\"size-full wp-image-8796\" src=\"data:image/svg+xml,%3Csvg%20xmlns=\'http://www.w3.org/2000/svg\'%20viewBox=\'0%200%20529%20529\'%3E%3C/svg%3E\" alt=\"reviewsach.net dang hoang giang dai duong den\" width=\"529\" height=\"529\" data-lazy-srcset=\"https://reviewsach.net/wp-content/uploads/2022/03/reviewsach.net-dang-hoang-giang-dai-duong-den.jpeg 529w, https://reviewsach.net/wp-content/uploads/2022/03/reviewsach.net-dang-hoang-giang-dai-duong-den-150x150.jpeg 150w, https://reviewsach.net/wp-content/uploads/2022/03/reviewsach.net-dang-hoang-giang-dai-duong-den-300x300.jpeg 300w, https://reviewsach.net/wp-content/uploads/2022/03/reviewsach.net-dang-hoang-giang-dai-duong-den-420x420.jpeg 420w\" data-lazy-sizes=\"(max-width: 529px) 100vw, 529px\" data-lazy-src=\"https://reviewsach.net/wp-content/uploads/2022/03/reviewsach.net-dang-hoang-giang-dai-duong-den.jpeg\"><noscript><img class=\"size-full wp-image-8796\" src=\"https://reviewsach.net/wp-content/uploads/2022/03/reviewsach.net-dang-hoang-giang-dai-duong-den.jpeg\" alt=\"reviewsach.net dang hoang giang dai duong den\" width=\"529\" height=\"529\" srcset=\"https://reviewsach.net/wp-content/uploads/2022/03/reviewsach.net-dang-hoang-giang-dai-duong-den.jpeg 529w, https://reviewsach.net/wp-content/uploads/2022/03/reviewsach.net-dang-hoang-giang-dai-duong-den-150x150.jpeg 150w, https://reviewsach.net/wp-content/uploads/2022/03/reviewsach.net-dang-hoang-giang-dai-duong-den-300x300.jpeg 300w, https://reviewsach.net/wp-content/uploads/2022/03/reviewsach.net-dang-hoang-giang-dai-duong-den-420x420.jpeg 420w\" sizes=\"(max-width: 529px) 100vw, 529px\" /></noscript><figcaption id=\"caption-attachment-8796\" class=\"wp-caption-text\">Ảnh: Instagram c.x.khoa</figcaption></figure>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400;\">Trầm cảm không phải bẩm sinh, những nạn nhân trầm cảm cũng không đối diện với nó theo một mô thức giống nhau. Nhưng kì lạ thay, giữa những con người có những phản ứng trái chiều đối với tổn thương trong quá khứ lại nảy sinh sự hòa hợp đầy “bạo lực và phá hủy”. Bố Thành đập phá cửa nhà, mẹ Thành làm như không rồi hàn gắn những đổ vỡ. Bố Thành “điên loạn”, phá phách, mẹ Thành nhẫn nhục, chịu đựng. Nỗi đau của bệnh nhân trầm cảm là một, nỗi đau của những người thân, gia đình và xã hội là mười.&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400;\">Những đoạn văn đầy tăm tối có thể khiến bạn đọc bị kích động mạnh. Nhưng có lẽ, đó là cách duy nhất để mỗi chúng ta có cái nhìn thực tế, khách quan, toàn diện hơn về thế giới của người trầm cảm. Tác giả làm tất cả điều đó với mong muốn kêu gọi mọi người có cái nhìn chân thật, có sự đồng cảm với hoàn cảnh của họ.&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400;\">Không chỉ đưa ra những câu chuyện thực tế, Đặng Hoàng Giang còn dày công nghiên cứu, tổng hợp kiến thức từ nhiều nguồn để xây dựng phần hai với những kiến thức cơ bản, thực tiễn, cần thiết cho tất cả mọi người. Suy cho cùng, xã hội đang chung sống với căn bệnh trầm cảm – bạn trung thành của Thần Chết. Trầm cảm không miễn trừ cho bất cứ ai và khi nó đến sẽ như một cơn bão vô hình, hủy diệt và cuốn trôi tất cả.&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400;\">Tựa như một bác sĩ với con dao lam sắc nhọn trong tay, Đặng Hoàng Giang dũng cảm mổ xẻ những vấn đề gai góc nhất, khó nhằn nhất của xã hội bằng ngôn từ đầy khoa học, khách quan mà dễ hiểu.&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400;\">Tôi không có nhiều kiến thức về tâm lí học, cũng không có nhiều trải nghiệm với bệnh nhân trầm cảm, nhưng tôi tiếp nhận cuốn sách với tất cả sự cảm thông và trân trọng đối với những bệnh nhân, người nhà của họ, và đặc biệt là tác giả cùng đội ngũ của ông. Trầm cảm nguy hại đến sức khỏe tinh thần, đe dọa trực tiếp đến tính mạng của bệnh nhân và những người xung quanh. </span></p>\r\n<figure id=\"attachment_8798\" aria-describedby=\"caption-attachment-8798\" style=\"width: 1080px\" class=\"wp-caption alignnone\"><img class=\"size-full wp-image-8798\" src=\"data:image/svg+xml,%3Csvg%20xmlns=\'http://www.w3.org/2000/svg\'%20viewBox=\'0%200%201080%201080\'%3E%3C/svg%3E\" alt=\"reviewsach.net dai duong den by dang hoang giang\" width=\"1080\" height=\"1080\" data-lazy-srcset=\"https://reviewsach.net/wp-content/uploads/2022/03/reviewsach.net-dai-duong-den-by-dang-hoang-giang.jpeg 1080w, https://reviewsach.net/wp-content/uploads/2022/03/reviewsach.net-dai-duong-den-by-dang-hoang-giang-150x150.jpeg 150w, https://reviewsach.net/wp-content/uploads/2022/03/reviewsach.net-dai-duong-den-by-dang-hoang-giang-768x768.jpeg 768w, https://reviewsach.net/wp-content/uploads/2022/03/reviewsach.net-dai-duong-den-by-dang-hoang-giang-300x300.jpeg 300w, https://reviewsach.net/wp-content/uploads/2022/03/reviewsach.net-dai-duong-den-by-dang-hoang-giang-600x600.jpeg 600w, https://reviewsach.net/wp-content/uploads/2022/03/reviewsach.net-dai-duong-den-by-dang-hoang-giang-696x696.jpeg 696w, https://reviewsach.net/wp-content/uploads/2022/03/reviewsach.net-dai-duong-den-by-dang-hoang-giang-1068x1068.jpeg 1068w, https://reviewsach.net/wp-content/uploads/2022/03/reviewsach.net-dai-duong-den-by-dang-hoang-giang-420x420.jpeg 420w, https://reviewsach.net/wp-content/uploads/2022/03/reviewsach.net-dai-duong-den-by-dang-hoang-giang-840x840.jpeg 840w\" data-lazy-sizes=\"(max-width: 1080px) 100vw, 1080px\" data-lazy-src=\"https://reviewsach.net/wp-content/uploads/2022/03/reviewsach.net-dai-duong-den-by-dang-hoang-giang.jpeg\"><noscript><img class=\"size-full wp-image-8798\" src=\"https://reviewsach.net/wp-content/uploads/2022/03/reviewsach.net-dai-duong-den-by-dang-hoang-giang.jpeg\" alt=\"reviewsach.net dai duong den by dang hoang giang\" width=\"1080\" height=\"1080\" srcset=\"https://reviewsach.net/wp-content/uploads/2022/03/reviewsach.net-dai-duong-den-by-dang-hoang-giang.jpeg 1080w, https://reviewsach.net/wp-content/uploads/2022/03/reviewsach.net-dai-duong-den-by-dang-hoang-giang-150x150.jpeg 150w, https://reviewsach.net/wp-content/uploads/2022/03/reviewsach.net-dai-duong-den-by-dang-hoang-giang-768x768.jpeg 768w, https://reviewsach.net/wp-content/uploads/2022/03/reviewsach.net-dai-duong-den-by-dang-hoang-giang-300x300.jpeg 300w, https://reviewsach.net/wp-content/uploads/2022/03/reviewsach.net-dai-duong-den-by-dang-hoang-giang-600x600.jpeg 600w, https://reviewsach.net/wp-content/uploads/2022/03/reviewsach.net-dai-duong-den-by-dang-hoang-giang-696x696.jpeg 696w, https://reviewsach.net/wp-content/uploads/2022/03/reviewsach.net-dai-duong-den-by-dang-hoang-giang-1068x1068.jpeg 1068w, https://reviewsach.net/wp-content/uploads/2022/03/reviewsach.net-dai-duong-den-by-dang-hoang-giang-420x420.jpeg 420w, https://reviewsach.net/wp-content/uploads/2022/03/reviewsach.net-dai-duong-den-by-dang-hoang-giang-840x840.jpeg 840w\" sizes=\"(max-width: 1080px) 100vw, 1080px\" /></noscript><figcaption id=\"caption-attachment-8798\" class=\"wp-caption-text\">Ảnh: Instagram danielscabin</figcaption></figure>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400;\">Ông vì thế viết nên cuốn sách này bằng giọng văn vừa sâu sắc, cảm thương, vừa khoa học, chính xác, có gì đó khác với các tác phẩm trước đó – phần nhiều là bình luận, phê phán, lên án những hiện tượng, sự kiện, con người xã hội. Thật cảm phục cho sự nhẫn nại, khoan dung của tác giả khi lắng tai nghe mọi thanh âm của cuộc sống, dũng cảm cất tiếng nói thay những người yếu thế, cảnh tỉnh xã hội về một căn bệnh sinh ra bởi chính nó. Người đọc buộc phải nhìn rất thẳng, rất sâu vào vấn đề được đặt ra, vì đối mặt và chống lại trầm cảm là nghĩa vụ của tất cả chúng ta.&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400;\">Tiến sĩ Đặng Hoàng Giang cũng là người khởi xướng đường dây nóng Ngày Mai, một hoạt động phi lợi nhuận nhằm cung cấp miển phí tham vấn tâm lý cho người trầm cảm, và nâng cao nhận thức của cộng đồng về sức khỏe tâm thần. Cuốn sách vì thế có giá trị thực tiễn rất cao.&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400;\">Có thể một số người có đủ sức vượt qua trầm cảm và sống một cuộc sống bình thản, an yên, hạnh phúc. Nhưng ai cũng biết điều đó là rất khó, phần nhiều, cuộc sống của những bệnh nhân trầm cảm sẽ mãi chìm trong đại dương tăm tối nếu không có ai đó tìm đến và lắng nghe câu chuyện của họ, cố gắng cứu lấy họ khi đang chấp chới. Sự ra đời của cuốn sách và sự đón nhận nhiệt liệt của độc giả có lẽ là tín hiệu đáng mừng, thể hiện sự quan tâm của mọi người đến căn bệnh trầm cảm. Dù chỉ le lói, ánh sáng của tình yêu thương, lòng vị tha hẳn vẫn tồn tại trong xã hội hối hả mà sự bận rộn của cuộc sống khiến chúng ta ít khi nói về nó.&nbsp;&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400;\">Không ai biết được cái kết của những nhân vật trong sách, cũng không ai có thể đảm bảo rằng, những phương pháp trị liệu được gợi ý sẽ có tác động thực tế hay không, đường dây nóng Ngày Mai sẽ giúp ích cho bao nhiêu người. Nhưng có một điều là chắc chắn: Chúng ta có thể ngăn chặn và khắc phục những hậu quả kinh hoàng của trầm cảm bằng nhiều cách khác nhau. Thiết nghĩ, cách tốt nhất là thể hiện sự quan tâm, yêu thương của mình đến mọi người xung quanh, đặc biệt là những người thân trong gia đình. Căn bệnh nào cũng cần được chữa trị. Ai ai cũng cần được lắng nghe, được yêu thương và giúp đỡ.&nbsp;</span></p>\r\n<p><strong>Link mua sách:</strong></p>\r\n<ul>\r\n<li>Tiki: <a href=\"https://shorten.asia/59Kj48CG\" target=\"_blank\" rel=\"noopener sponsored nofollow external noreferrer\" data-wpel-link=\"external\">https://shorten.asia/59Kj48CG</a></li>\r\n<li>Fahasa: <a href=\"https://shorten.asia/CtHJVXRQ\" target=\"_blank\" rel=\"noopener sponsored nofollow external noreferrer\" data-wpel-link=\"external\">https://shorten.asia/CtHJVXRQ</a></li>\r\n<li>Shopee: <a href=\"https://shorten.asia/vsWmXhgC\" target=\"_blank\" rel=\"noopener sponsored nofollow external noreferrer\" data-wpel-link=\"external\">https://shorten.asia/vsWmXhgC</a></li>\r\n<li>Lazada: <a href=\"https://shorten.asia/MS6QkUqt\" target=\"_blank\" rel=\"noopener sponsored nofollow external noreferrer\" data-wpel-link=\"external\">https://shorten.asia/MS6QkUqt</a></li>\r\n</ul>\r\n<div class=\"saboxplugin-wrap\" itemtype=\"http://schema.org/Person\" itemscope=\"\" itemprop=\"author\"><div class=\"saboxplugin-tab\"><div class=\"saboxplugin-gravatar\"><img src=\"data:image/svg+xml,%3Csvg%20xmlns=\'http://www.w3.org/2000/svg\'%20viewBox=\'0%200%20100%20100\'%3E%3C/svg%3E\" width=\"100\" height=\"100\" alt=\"Bà Gấu\" itemprop=\"image\" data-lazy-src=\"https://reviewsach.net/wp-content/uploads/2018/12/Gấu.jpg\"><noscript><img src=\"https://reviewsach.net/wp-content/uploads/2018/12/Gấu.jpg\" width=\"100\"  height=\"100\" alt=\"Bà Gấu\" itemprop=\"image\"></noscript></div><div class=\"saboxplugin-authorname\"><a href=\"https://reviewsach.net/author/gaugau/\" class=\"vcard author\" rel=\"author nofollow noopener noreferrer\" itemprop=\"url\" data-wpel-link=\"internal\"><span class=\"fn\" itemprop=\"name\">Gấu Mèo</span></a></div><div class=\"saboxplugin-desc\"><div itemprop=\"description\"><p>Gấu là một tác giả lặng thầm dễ thương nhất, không yêu cầu gì nhiều ngoài chút nhuận bút cỏn con đủ để uống một ly sinh tố giữa trưa hè nóng bức</p>\r\n<p>Đùa thôi, nói vậy chứ Gấu Mèo phụ trách nhiều chuyên mục lắm đấy nhé</p>\r\n</div></div><div class=\"clearfix\"></div></div></div>        </div>\r\n\r\n\r\n        <footer>\r\n                        \r\n            <div class=\"td-post-source-tags\">\r\n                                            </div>\r\n\r\n                        <div class=\"td-block-row td-post-next-prev\"><div class=\"td-block-span6 td-post-prev-post\"><div class=\"td-post-next-prev-content\"><span>Previous article</span><a href=\"https://reviewsach.net/toi-la-be-to/\" data-wpel-link=\"internal\" rel=\"nofollow noopener noreferrer\">“Tôi là Bê tô” – Thế giới thần tiên dung dị và đời thường</a></div></div><div class=\"td-next-prev-separator\"></div><div class=\"td-block-span6 td-post-next-post\"><div class=\"td-post-next-prev-content\"><span>Next article</span><a href=\"https://reviewsach.net/nhung-ke-dang-ngo/\" data-wpel-link=\"internal\" rel=\"nofollow noopener noreferrer\">Những kẻ đáng ngờ (Higashino Keigo) – Những mẩu truyện ngắn mang nội dung trọn vẹn</a></div></div></div>            <div class=\"td-author-name vcard author\" style=\"display: none\"><span class=\"fn\"><a href=\"https://reviewsach.net/author/gaugau/\" data-wpel-link=\"internal\" rel=\"nofollow noopener noreferrer\">Gấu Mèo</a></span></div>	        <span class=\"td-page-meta\" itemprop=\"author\" itemscope=\"\" itemtype=\"https://schema.org/Person\"><meta itemprop=\"name\" content=\"Gấu Mèo\"><meta itemprop=\"url\" content=\"https://reviewsach.net/author/gaugau/\"></span><meta itemprop=\"datePublished\" content=\"2022-03-29T09:37:49+07:00\"><meta itemprop=\"dateModified\" content=\"2022-03-31T08:30:53+07:00\"><meta itemscope=\"\" itemprop=\"mainEntityOfPage\" itemtype=\"https://schema.org/WebPage\" itemid=\"https://reviewsach.net/dai-duong-den/\"><span class=\"td-page-meta\" itemprop=\"publisher\" itemscope=\"\" itemtype=\"https://schema.org/Organization\"><span class=\"td-page-meta\" itemprop=\"logo\" itemscope=\"\" itemtype=\"https://schema.org/ImageObject\"><meta itemprop=\"url\" content=\"https://reviewsach.net/wp-content/uploads/2021/04/reviewsach.net-logo.png\"></span><meta itemprop=\"name\" content=\"Review sách - Reviewsach.net\"></span><meta itemprop=\"headline \" content=\"Đại dương đen: Ánh sáng giữa đại dương tăm tối\"><span class=\"td-page-meta\" itemprop=\"image\" itemscope=\"\" itemtype=\"https://schema.org/ImageObject\"><meta itemprop=\"url\" content=\"https://reviewsach.net/wp-content/uploads/2022/03/cover-sach-dai-duong-den.jpeg\"><meta itemprop=\"width\" content=\"533\"><meta itemprop=\"height\" content=\"399\"></span>        </footer>\r\n\r\n    \r\n                                </div>', NULL, NULL, 'Tiếp nối thành công từ những cuốn sách trước đó như “Bức xúc không làm ta vô can”, “Thiện – ác và smartphone”, “Tìm mình trong thế giới hậu tuổi thơ”, Tiến sĩ Đặng Hoàng Giang công bố cuốn sách mang tên “Đại dương đen” – kết tinh hành trình đầy nhọc nhằn và nhẫn nại của tác giả với người trầm cảm trong suốt hai năm ròng. Cuốn sách ghi lại tiếng nói đầy xót xa, ám ảnh của những người đang ngấp ngoải trong “đại dương” trầm cảm.\r\n', NULL),(2, 'Khỏe lên, trẻ lại – Lục điệu chân kinh dành cho các cao thủ văn phòng\r\n', 'https://reviewsach.net/wp-content/uploads/2022/07/Khoe-len-tre-lai-reviewsachnet-featuredpic-324x235.jpeg\r\n', '<div class=\"td-pb-span8 td-main-content\" role=\"main\">\r\n                                <div class=\"td-ss-main-content\">\r\n                                    \r\n        \r\n\r\n        <div class=\"td-post-content tagdiv-type\">\r\n            \r\n            <h2 style=\"text-align: justify;\"><strong>Đả thông tư tưởng “nhưng mà” “tại vì”, hướng đến mục tiêu “tập như không tập” để việc rèn luyện thân thể trở thành nhịp điệu vui vẻ trong bài ca cuộc sống thường nhật… là những gì cuốn sách nhỏ “Khỏe lên, trẻ lại” của Huỳnh Kim Tước mong muốn mang đến cho bạn đọc nói chung và nhân viên văn phòng nói riêng.</strong></h2>\r\n<p style=\"text-align: justify;\"><img class=\"alignnone size-full wp-image-8982 entered lazyloaded\" src=\"https://reviewsach.net/wp-content/uploads/2022/07/Khoe-len-tre-lai-reviewsachnet.jpeg\" alt=\"Khỏe lên trẻ lại reviewsachnet\" width=\"1200\" height=\"900\" data-lazy-srcset=\"https://reviewsach.net/wp-content/uploads/2022/07/Khoe-len-tre-lai-reviewsachnet.jpeg 1200w, https://reviewsach.net/wp-content/uploads/2022/07/Khoe-len-tre-lai-reviewsachnet-768x576.jpeg 768w, https://reviewsach.net/wp-content/uploads/2022/07/Khoe-len-tre-lai-reviewsachnet-150x113.jpeg 150w, https://reviewsach.net/wp-content/uploads/2022/07/Khoe-len-tre-lai-reviewsachnet-300x225.jpeg 300w, https://reviewsach.net/wp-content/uploads/2022/07/Khoe-len-tre-lai-reviewsachnet-600x450.jpeg 600w, https://reviewsach.net/wp-content/uploads/2022/07/Khoe-len-tre-lai-reviewsachnet-696x522.jpeg 696w, https://reviewsach.net/wp-content/uploads/2022/07/Khoe-len-tre-lai-reviewsachnet-1068x801.jpeg 1068w, https://reviewsach.net/wp-content/uploads/2022/07/Khoe-len-tre-lai-reviewsachnet-560x420.jpeg 560w, https://reviewsach.net/wp-content/uploads/2022/07/Khoe-len-tre-lai-reviewsachnet-1120x840.jpeg 1120w, https://reviewsach.net/wp-content/uploads/2022/07/Khoe-len-tre-lai-reviewsachnet-80x60.jpeg 80w, https://reviewsach.net/wp-content/uploads/2022/07/Khoe-len-tre-lai-reviewsachnet-160x120.jpeg 160w, https://reviewsach.net/wp-content/uploads/2022/07/Khoe-len-tre-lai-reviewsachnet-265x198.jpeg 265w\" data-lazy-sizes=\"(max-width: 1200px) 100vw, 1200px\" data-lazy-src=\"https://reviewsach.net/wp-content/uploads/2022/07/Khoe-len-tre-lai-reviewsachnet.jpeg\" data-ll-status=\"loaded\" sizes=\"(max-width: 1200px) 100vw, 1200px\" srcset=\"https://reviewsach.net/wp-content/uploads/2022/07/Khoe-len-tre-lai-reviewsachnet.jpeg 1200w, https://reviewsach.net/wp-content/uploads/2022/07/Khoe-len-tre-lai-reviewsachnet-768x576.jpeg 768w, https://reviewsach.net/wp-content/uploads/2022/07/Khoe-len-tre-lai-reviewsachnet-150x113.jpeg 150w, https://reviewsach.net/wp-content/uploads/2022/07/Khoe-len-tre-lai-reviewsachnet-300x225.jpeg 300w, https://reviewsach.net/wp-content/uploads/2022/07/Khoe-len-tre-lai-reviewsachnet-600x450.jpeg 600w, https://reviewsach.net/wp-content/uploads/2022/07/Khoe-len-tre-lai-reviewsachnet-696x522.jpeg 696w, https://reviewsach.net/wp-content/uploads/2022/07/Khoe-len-tre-lai-reviewsachnet-1068x801.jpeg 1068w, https://reviewsach.net/wp-content/uploads/2022/07/Khoe-len-tre-lai-reviewsachnet-560x420.jpeg 560w, https://reviewsach.net/wp-content/uploads/2022/07/Khoe-len-tre-lai-reviewsachnet-1120x840.jpeg 1120w, https://reviewsach.net/wp-content/uploads/2022/07/Khoe-len-tre-lai-reviewsachnet-80x60.jpeg 80w, https://reviewsach.net/wp-content/uploads/2022/07/Khoe-len-tre-lai-reviewsachnet-160x120.jpeg 160w, https://reviewsach.net/wp-content/uploads/2022/07/Khoe-len-tre-lai-reviewsachnet-265x198.jpeg 265w\"><noscript><img class=\"alignnone size-full wp-image-8982\" src=\"https://reviewsach.net/wp-content/uploads/2022/07/Khoe-len-tre-lai-reviewsachnet.jpeg\" alt=\"Khỏe lên trẻ lại reviewsachnet\" width=\"1200\" height=\"900\" srcset=\"https://reviewsach.net/wp-content/uploads/2022/07/Khoe-len-tre-lai-reviewsachnet.jpeg 1200w, https://reviewsach.net/wp-content/uploads/2022/07/Khoe-len-tre-lai-reviewsachnet-768x576.jpeg 768w, https://reviewsach.net/wp-content/uploads/2022/07/Khoe-len-tre-lai-reviewsachnet-150x113.jpeg 150w, https://reviewsach.net/wp-content/uploads/2022/07/Khoe-len-tre-lai-reviewsachnet-300x225.jpeg 300w, https://reviewsach.net/wp-content/uploads/2022/07/Khoe-len-tre-lai-reviewsachnet-600x450.jpeg 600w, https://reviewsach.net/wp-content/uploads/2022/07/Khoe-len-tre-lai-reviewsachnet-696x522.jpeg 696w, https://reviewsach.net/wp-content/uploads/2022/07/Khoe-len-tre-lai-reviewsachnet-1068x801.jpeg 1068w, https://reviewsach.net/wp-content/uploads/2022/07/Khoe-len-tre-lai-reviewsachnet-560x420.jpeg 560w, https://reviewsach.net/wp-content/uploads/2022/07/Khoe-len-tre-lai-reviewsachnet-1120x840.jpeg 1120w, https://reviewsach.net/wp-content/uploads/2022/07/Khoe-len-tre-lai-reviewsachnet-80x60.jpeg 80w, https://reviewsach.net/wp-content/uploads/2022/07/Khoe-len-tre-lai-reviewsachnet-160x120.jpeg 160w, https://reviewsach.net/wp-content/uploads/2022/07/Khoe-len-tre-lai-reviewsachnet-265x198.jpeg 265w\" sizes=\"(max-width: 1200px) 100vw, 1200px\" /></noscript></p>\r\n<p style=\"text-align: justify;\"><strong>Đọc thêm:</strong></p>\r\n<ul style=\"text-align: justify;\">\r\n<li class=\"entry-title\"><a href=\"https://reviewsach.net/chay-troi-khong-khoi-dau/\" data-wpel-link=\"internal\" rel=\"nofollow noopener noreferrer\">Chạy trời không khỏi đau – Con người ai cũng mong manh như ai.</a></li>\r\n<li class=\"entry-title\"><a href=\"https://reviewsach.net/nhat-ky-covid-va-nhung-chuyen-chua-ke/\" data-wpel-link=\"internal\" rel=\"nofollow noopener noreferrer\">Nhật ký Covid và những chuyện chưa kể – Tâm sự của người chiến sĩ áo trắng tuyến đầu chống dịch.</a></li>\r\n</ul>\r\n<h2 style=\"text-align: justify;\"><strong>Dùng ngôn từ thú vị nhất diễn giải nội dung ngắn gọn nhất!</strong></h2>\r\n<p style=\"text-align: justify;\">Với tên đầy đủ là “Khỏe lên, trẻ lại – Lục điệu chân kinh dành cho các cao thủ văn phòng”, tác phẩm gây khác biệt với những đầu sách cùng đề tài bởi vẻ kiếm hiệp hoàn toàn mới lạ, từ bìa sách cho đến cách triển khai nội dung.</p>\r\n<p style=\"text-align: justify;\">Được biết, lý lo là tác giả Huỳnh Kim Tước mê đọc tiểu thuyết Kim Dung, đồng thời có thói quen gọi những nhiệm vụ khó nhằn bằng sở thích và xem chúng như trò chơi đầy thú vị thay vì bổn phận phải làm. Sự thật là việc rèn luyện thể lực cũng từng khiến tác giả nhiều lần nản chí. Dường như tâm lý chung của con người là muốn khỏe nhưng lười tập để khỏe, và việc đả thông tư tưởng này là cấp thiết trước tất thảy việc tập như thế nào.</p>\r\n<p style=\"text-align: justify;\">Cuốn sách đi thẳng vào hai vấn đề. Một là giải quyết những trở lực trong việc luyện tập (Hóa giải “văn phòng tam hắc chưởng”): không có thời gian, không có không gian và phương tiện tập luyện tại nhà phù hợp, không có cách duy trì luyện tập lâu dài. Hai là tập như thế nào (Lục điệu chân kinh – 6 bí kíp đắc thủ võ công): ăn đúng món, ngủ đúng mức, thở đúng phép, luyện đúng giờ, tập đúng cách, dượt đúng điệu.</p>\r\n<p style=\"text-align: justify;\">“Khỏe lên, trẻ lại” đưa ra một góc nhìn, một cách tiếp cận, một phương thức rèn luyện dễ dàng và tự nhiên: 30 phút mỗi ngày với 6 động tác đơn giản và một số thói quen điều độ.</p>\r\n<p style=\"text-align: justify;\">Hẳn là có nhiều người từng quyết tâm tập thể dục hằng ngày, cũng đã từng viện cho bản thân không ít cái cớ để thoái thác việc rèn luyện, thậm chí thường xuyên bỏ tập dù đã mua thẻ hội viên ở các phòng tập hay sắm máy tự tập ở nhà. Điểm sáng của cuốn sách “Khỏe lên, trẻ lại” có lẽ là đả thông tư tưởng, giải quyết vấn đề làm thế nào để duy trì luyện tập lâu dài, và phương châm tập như không tập, không đến phòng tập mà để phòng tập đến với mình.</p>\r\n<p style=\"text-align: justify;\">Huỳnh Kim Tước nói về tập như không tập, không phải tập quơ quào được chăng hay chớ mà anh tập những động tác đơn giản nhưng hiệu quả, đồng thời biến việc rèn luyện thành thói quen hằng ngày như đánh răng rửa mặt thay vì xem nó phục vụ động lực lớn lao như nào. Châm lửa bằng động lực, giữ lửa bằng thói quen.</p>\r\n<p style=\"text-align: justify;\"><em>“Chúng ta là cái chúng ta lặp đi lặp lại. Sự xuất sắc không phải là một hành động mà là một thói quen.”</em> – Nhà triết học Aristotle.</p>\r\n<h2 style=\"text-align: justify;\"><strong>Một cuốn sổ tay rèn luyện sức khỏe không đến từ chuyên gia.</strong></h2>\r\n<p style=\"text-align: justify;\">Ngoài những khu vực công cộng sớm được mọi người tận dụng làm nơi tập tành nâng cao sức khỏe, thì ngày nay, hàng loạt các trung tâm thể dục thẩm mỹ ra đời với đa dạng bộ môn như gym, yoga, dance,… nhằm đáp ứng nhu cầu rèn luyện và làm đẹp cơ thể ngày càng tăng. Tuy nhiên, không phải tất cả mọi người đều có đủ thời gian.</p>\r\n<p style=\"text-align: justify;\">Có một bộ phận doanh nhân và nhân viên văn phòng khó có thể dành ra thời gian để chạy bộ công viên hay đến trung tâm thể dục, vì lịch trình bận rộn, vì công tác thường xuyên… Lại có không ít người có thời gian nhưng ngại đến phòng tập, ngại tốn tiền, ngại mệt… Nguyên nhân đơn giản thô bạo nhất là lười! “Khỏe lên, trẻ lại” của Huỳnh Kim Tước là cuốn sổ tay rèn luyện sức khỏe dành cho nhóm người này.</p>\r\n<figure id=\"attachment_8983\" aria-describedby=\"caption-attachment-8983\" style=\"width: 1200px\" class=\"wp-caption alignnone\"><img class=\"size-full wp-image-8983\" src=\"data:image/svg+xml,%3Csvg%20xmlns=\'http://www.w3.org/2000/svg\'%20viewBox=\'0%200%201200%20799\'%3E%3C/svg%3E\" alt=\"Tác giả Huỳnh Kim Tước reviewsachnet\" width=\"1200\" height=\"799\" data-lazy-srcset=\"https://reviewsach.net/wp-content/uploads/2022/07/Tac-gia-Huynh-Kim-Tuoc-reviewsachnet.jpg 1200w, https://reviewsach.net/wp-content/uploads/2022/07/Tac-gia-Huynh-Kim-Tuoc-reviewsachnet-768x511.jpg 768w, https://reviewsach.net/wp-content/uploads/2022/07/Tac-gia-Huynh-Kim-Tuoc-reviewsachnet-150x100.jpg 150w, https://reviewsach.net/wp-content/uploads/2022/07/Tac-gia-Huynh-Kim-Tuoc-reviewsachnet-300x200.jpg 300w, https://reviewsach.net/wp-content/uploads/2022/07/Tac-gia-Huynh-Kim-Tuoc-reviewsachnet-600x400.jpg 600w, https://reviewsach.net/wp-content/uploads/2022/07/Tac-gia-Huynh-Kim-Tuoc-reviewsachnet-696x463.jpg 696w, https://reviewsach.net/wp-content/uploads/2022/07/Tac-gia-Huynh-Kim-Tuoc-reviewsachnet-1068x711.jpg 1068w, https://reviewsach.net/wp-content/uploads/2022/07/Tac-gia-Huynh-Kim-Tuoc-reviewsachnet-631x420.jpg 631w\" data-lazy-sizes=\"(max-width: 1200px) 100vw, 1200px\" data-lazy-src=\"https://reviewsach.net/wp-content/uploads/2022/07/Tac-gia-Huynh-Kim-Tuoc-reviewsachnet.jpg\"><noscript><img class=\"size-full wp-image-8983\" src=\"https://reviewsach.net/wp-content/uploads/2022/07/Tac-gia-Huynh-Kim-Tuoc-reviewsachnet.jpg\" alt=\"Tác giả Huỳnh Kim Tước reviewsachnet\" width=\"1200\" height=\"799\" srcset=\"https://reviewsach.net/wp-content/uploads/2022/07/Tac-gia-Huynh-Kim-Tuoc-reviewsachnet.jpg 1200w, https://reviewsach.net/wp-content/uploads/2022/07/Tac-gia-Huynh-Kim-Tuoc-reviewsachnet-768x511.jpg 768w, https://reviewsach.net/wp-content/uploads/2022/07/Tac-gia-Huynh-Kim-Tuoc-reviewsachnet-150x100.jpg 150w, https://reviewsach.net/wp-content/uploads/2022/07/Tac-gia-Huynh-Kim-Tuoc-reviewsachnet-300x200.jpg 300w, https://reviewsach.net/wp-content/uploads/2022/07/Tac-gia-Huynh-Kim-Tuoc-reviewsachnet-600x400.jpg 600w, https://reviewsach.net/wp-content/uploads/2022/07/Tac-gia-Huynh-Kim-Tuoc-reviewsachnet-696x463.jpg 696w, https://reviewsach.net/wp-content/uploads/2022/07/Tac-gia-Huynh-Kim-Tuoc-reviewsachnet-1068x711.jpg 1068w, https://reviewsach.net/wp-content/uploads/2022/07/Tac-gia-Huynh-Kim-Tuoc-reviewsachnet-631x420.jpg 631w\" sizes=\"(max-width: 1200px) 100vw, 1200px\" /></noscript><figcaption id=\"caption-attachment-8983\" class=\"wp-caption-text\">Tác giả Huỳnh Kim Tước (Ảnh từ Facebook cá nhân Tuoc Huynh)</figcaption></figure>\r\n<p style=\"text-align: justify;\">Huỳnh Kim Tước là một cổ cồn trắng chính hiệu với kinh nghiệm thực tiễn nhiều năm mài quần trên ghế văn phòng, từ ghế chuyên viên quản lý trong các liên doanh như Phú Mỹ Hưng và Mercedes-Benz tại Việt Nam, đến ghế cố vấn cho Google thị trường Việt Nam, hay ghế điều phối viên chương trình AmeriCorps của cựu Tổng thống Bill Clinton, và ghế cố vấn Facebook khu vực Đông Dương… có lẽ nên tính thêm cả ghế máy bay nữa vì anh đi công tác khá thường xuyên. Với lịch làm việc khá căng, ban ngày gặp gỡ và làm việc với đối tác trong khu vực Đông Nam Á, ban đêm làm việc với công ty ở Mỹ đến một, hai giờ sáng, ấy vậy mà Huỳnh Kim Tước vẫn luôn giữ được vẻ ngoài trẻ trung hơn tuổi và sức làm việc dẻo dai, tinh thần sáng láng.</p>\r\n<p style=\"text-align: justify;\">“Khỏe lên, trẻ lại” là đúc kết và chiêm nghiệm từ việc rèn luyện cá nhân của anh, bắt đầu tình cờ từ những cuộc trò chuyện thân tình với đối tác, thường là các CEO tuổi trung niên, chia sẻ về cách giữ phong độ thể thao giữa bề bộn công việc.</p>\r\n<p style=\"text-align: justify;\">Vì là kinh nghiệm rèn luyện sức khỏe cá nhân lại không đến từ chuyên gia, và chỉ hướng đến một nhóm đối tượng doanh nhân và nhân viên văn phòng, “Khỏe lên, trẻ lại” khá kén người đọc. Nhưng nếu rà đúng tần số, luyện bí kíp một cách thành thật nghiêm chỉnh, cuốn sách sẽ mở ra một chân trời mới. Đã là con người, ai lại không mong muốn khỏe lên và trẻ lại đâu?</p>\r\n<p><strong>Link mua sách:</strong></p>\r\n<ul>\r\n<li>Lazada: <a href=\"https://shorten.asia/NYYSYcSW\" target=\"_blank\" rel=\"noopener sponsored nofollow external noreferrer\" data-wpel-link=\"external\">https://shorten.asia/NYYSYcSW</a></li>\r\n</ul>\r\n<div class=\"saboxplugin-wrap\" itemtype=\"http://schema.org/Person\" itemscope=\"\" itemprop=\"author\"><div class=\"saboxplugin-tab\"><div class=\"saboxplugin-gravatar\"><img src=\"data:image/svg+xml,%3Csvg%20xmlns=\'http://www.w3.org/2000/svg\'%20viewBox=\'0%200%20100%20100\'%3E%3C/svg%3E\" width=\"100\" height=\"100\" alt=\"Duyên - reviewsach.net Author\" itemprop=\"image\" data-lazy-src=\"https://reviewsach.net/wp-content/uploads/2020/10/duyen.jpg\"><noscript><img src=\"https://reviewsach.net/wp-content/uploads/2020/10/duyen.jpg\" width=\"100\"  height=\"100\" alt=\"Duyên - reviewsach.net Author\" itemprop=\"image\"></noscript></div><div class=\"saboxplugin-authorname\"><a href=\"https://reviewsach.net/author/duyen/\" class=\"vcard author\" rel=\"author nofollow noopener noreferrer\" itemprop=\"url\" data-wpel-link=\"internal\"><span class=\"fn\" itemprop=\"name\">Duyên Bùi</span></a></div><div class=\"saboxplugin-desc\"><div itemprop=\"description\"><p><span id=\"fbPhotoSnowliftCaption\" class=\"fbPhotosPhotoCaption\" data-ft=\"{\"><span class=\"hasCaption\">A lonely genius said to me: “<em>You know the way. And your curiosity is what sets you apart from the rest. Use your thoughts your hands your hunger for knowledge. Because these things are yours and yours alone.”</em></span></span></p>\r\n<p>That’s the reason why I love reading books.</p>\r\n<div id=\"fbPhotoSnowliftProductMiniListHscroll\" class=\"fbCommerceProductMiniListHscroll\">Tôi là Duyên, yêu văn học và lịch sử, thích Vũ Trọng Phụng.</div>\r\n</div></div><div class=\"clearfix\"></div><div class=\"saboxplugin-socials \"><a target=\"_blank\" href=\"https://www.facebook.com/duyenkoko0810\" aria-label=\"Facebook\" rel=\"noopener nofollow external noreferrer sponsored\" class=\"saboxplugin-icon-grey\" data-wpel-link=\"external\"><svg aria-hidden=\"true\" class=\"sab-facebook\" role=\"img\" xmlns=\"http://www.w3.org/2000/svg\" viewBox=\"0 0 264 512\"><path fill=\"currentColor\" d=\"M76.7 512V283H0v-91h76.7v-71.7C76.7 42.4 124.3 0 193.8 0c33.3 0 61.9 2.5 70.2 3.6V85h-48.2c-37.8 0-45.1 18-45.1 44.3V192H256l-11.7 91h-73.6v229\"></path></svg></a></div></div></div>        </div>\r\n\r\n\r\n        <footer>\r\n                        \r\n            <div class=\"td-post-source-tags\">\r\n                                            </div>\r\n\r\n                        <div class=\"td-block-row td-post-next-prev\"><div class=\"td-block-span6 td-post-prev-post\"><div class=\"td-post-next-prev-content\"><span>Previous article</span><a href=\"https://reviewsach.net/tu-dao-ay/\" data-wpel-link=\"internal\" rel=\"nofollow noopener noreferrer\">Từ dạo ấy (Natsume Soseki) – Câu chuyện về cuộc khủng hoảng bản sắc nam&nbsp;</a></div></div><div class=\"td-next-prev-separator\"></div><div class=\"td-block-span6 td-post-next-post\"><div class=\"td-post-next-prev-content\"><span>Next article</span><a href=\"https://reviewsach.net/dau-oi-bay-di/\" data-wpel-link=\"internal\" rel=\"nofollow noopener noreferrer\">Đau ơi bay đi! (Miaki Sugaru) – Cho những thương tổn, bay về hư vô</a></div></div></div>            <div class=\"td-author-name vcard author\" style=\"display: none\"><span class=\"fn\"><a href=\"https://reviewsach.net/author/duyen/\" data-wpel-link=\"internal\" rel=\"nofollow noopener noreferrer\">Duyên Bùi</a></span></div>	        <span class=\"td-page-meta\" itemprop=\"author\" itemscope=\"\" itemtype=\"https://schema.org/Person\"><meta itemprop=\"name\" content=\"Duyên Bùi\"><meta itemprop=\"url\" content=\"https://reviewsach.net/author/duyen/\"></span><meta itemprop=\"datePublished\" content=\"2022-07-17T07:41:31+07:00\"><meta itemprop=\"dateModified\" content=\"2022-07-23T10:08:24+07:00\"><meta itemscope=\"\" itemprop=\"mainEntityOfPage\" itemtype=\"https://schema.org/WebPage\" itemid=\"https://reviewsach.net/khoe-len-tre-lai/\"><span class=\"td-page-meta\" itemprop=\"publisher\" itemscope=\"\" itemtype=\"https://schema.org/Organization\"><span class=\"td-page-meta\" itemprop=\"logo\" itemscope=\"\" itemtype=\"https://schema.org/ImageObject\"><meta itemprop=\"url\" content=\"https://reviewsach.net/wp-content/uploads/2021/04/reviewsach.net-logo.png\"></span><meta itemprop=\"name\" content=\"Review sách - Reviewsach.net\"></span><meta itemprop=\"headline \" content=\"Khỏe lên, trẻ lại – Lục điệu chân kinh dành cho các cao thủ văn phòng.\"><span class=\"td-page-meta\" itemprop=\"image\" itemscope=\"\" itemtype=\"https://schema.org/ImageObject\"><meta itemprop=\"url\" content=\"https://reviewsach.net/wp-content/uploads/2022/07/Khoe-len-tre-lai-reviewsachnet-featuredpic.jpeg\"><meta itemprop=\"width\" content=\"533\"><meta itemprop=\"height\" content=\"400\"></span>        </footer>\r\n\r\n    \r\n                                </div>\r\n                            </div>', NULL, NULL, 'Đả thông tư tưởng “nhưng mà” “tại vì”, hướng đến mục tiêu “tập như không tập” để việc rèn luyện thân thể trở thành nhịp điệu vui vẻ trong bài ca cuộc sống thường nhật… là những gì cuốn sách nhỏ “Khỏe lên, trẻ lại” của Huỳnh Kim Tước mong muốn mang đến cho bạn đọc nói chung và nhân viên văn phòng nói riêng.\r\nĐả thông tư tưởng “nhưng mà” “tại vì”.\r\n', NULL),(3, 'Nguồn cội – Tiếng nói của một bản dạng văn hóa linh hoạt.\r\n', 'https://reviewsach.net/wp-content/uploads/2022/03/Featured-Picture-Nguon-coi-reviewsachonly-324x235.jpg\r\n', '<div class=\"td-pb-span8 td-main-content\" role=\"main\">\r\n                                <div class=\"td-ss-main-content\">\r\n                                    \r\n        \r\n\r\n        <div class=\"td-post-content tagdiv-type\">\r\n            \r\n            <h2 style=\"text-align: justify\"><strong>Cũng là “<a href=\"https://reviewsach.net/nguon-coi/\" data-wpel-link=\"internal\" rel=\"nofollow noopener noreferrer\">Nguồn cội</a>”, nhưng là “Nguồn cội” của Đan Thy chứ không phải của Dan Brown, và hẳn nhiên, đây không phải là một cuốn tiểu thuyết kinh dị bí ẩn. “Nguồn cội” của Nguyễn Đan Thy (Thy Nguyen) là một tuyển tập những truyện ngắn và thơ, viết về văn hóa và bản sắc cá nhân, về bản dạng văn hóa phức tạp đã từng chênh vênh nhưng linh hoạt tìm cách thoát khỏi vùng u minh, để tự tin hòa nhập.</strong></h2>\r\n<p style=\"text-align: right\"><em>&nbsp;“Tôi tìm thấy yên bình khi mang hai quốc tịch,</em></p>\r\n<p style=\"text-align: right\"><em>trong đa dạng căn tính, một vẻ đẹp lộ hình.”</em></p>\r\n<figure id=\"attachment_8661\" aria-describedby=\"caption-attachment-8661\" style=\"width: 1080px\" class=\"wp-caption alignnone\"><img class=\"size-full wp-image-8661 entered lazyloaded\" src=\"https://reviewsach.net/wp-content/uploads/2022/03/Anh-thekibrary_-Nguon-coi-reviewsachonly.jpg\" alt=\"Ảnh thekibrary_ Nguồn cội reviewsachonly\" width=\"1080\" height=\"1080\" data-lazy-srcset=\"https://reviewsach.net/wp-content/uploads/2022/03/Anh-thekibrary_-Nguon-coi-reviewsachonly.jpg 1080w, https://reviewsach.net/wp-content/uploads/2022/03/Anh-thekibrary_-Nguon-coi-reviewsachonly-150x150.jpg 150w, https://reviewsach.net/wp-content/uploads/2022/03/Anh-thekibrary_-Nguon-coi-reviewsachonly-768x768.jpg 768w, https://reviewsach.net/wp-content/uploads/2022/03/Anh-thekibrary_-Nguon-coi-reviewsachonly-300x300.jpg 300w, https://reviewsach.net/wp-content/uploads/2022/03/Anh-thekibrary_-Nguon-coi-reviewsachonly-600x600.jpg 600w, https://reviewsach.net/wp-content/uploads/2022/03/Anh-thekibrary_-Nguon-coi-reviewsachonly-696x696.jpg 696w, https://reviewsach.net/wp-content/uploads/2022/03/Anh-thekibrary_-Nguon-coi-reviewsachonly-1068x1068.jpg 1068w, https://reviewsach.net/wp-content/uploads/2022/03/Anh-thekibrary_-Nguon-coi-reviewsachonly-420x420.jpg 420w, https://reviewsach.net/wp-content/uploads/2022/03/Anh-thekibrary_-Nguon-coi-reviewsachonly-840x840.jpg 840w\" data-lazy-sizes=\"(max-width: 1080px) 100vw, 1080px\" data-lazy-src=\"https://reviewsach.net/wp-content/uploads/2022/03/Anh-thekibrary_-Nguon-coi-reviewsachonly.jpg\" data-ll-status=\"loaded\" sizes=\"(max-width: 1080px) 100vw, 1080px\" srcset=\"https://reviewsach.net/wp-content/uploads/2022/03/Anh-thekibrary_-Nguon-coi-reviewsachonly.jpg 1080w, https://reviewsach.net/wp-content/uploads/2022/03/Anh-thekibrary_-Nguon-coi-reviewsachonly-150x150.jpg 150w, https://reviewsach.net/wp-content/uploads/2022/03/Anh-thekibrary_-Nguon-coi-reviewsachonly-768x768.jpg 768w, https://reviewsach.net/wp-content/uploads/2022/03/Anh-thekibrary_-Nguon-coi-reviewsachonly-300x300.jpg 300w, https://reviewsach.net/wp-content/uploads/2022/03/Anh-thekibrary_-Nguon-coi-reviewsachonly-600x600.jpg 600w, https://reviewsach.net/wp-content/uploads/2022/03/Anh-thekibrary_-Nguon-coi-reviewsachonly-696x696.jpg 696w, https://reviewsach.net/wp-content/uploads/2022/03/Anh-thekibrary_-Nguon-coi-reviewsachonly-1068x1068.jpg 1068w, https://reviewsach.net/wp-content/uploads/2022/03/Anh-thekibrary_-Nguon-coi-reviewsachonly-420x420.jpg 420w, https://reviewsach.net/wp-content/uploads/2022/03/Anh-thekibrary_-Nguon-coi-reviewsachonly-840x840.jpg 840w\"><noscript><img class=\"size-full wp-image-8661\" src=\"https://reviewsach.net/wp-content/uploads/2022/03/Anh-thekibrary_-Nguon-coi-reviewsachonly.jpg\" alt=\"Ảnh thekibrary_ Nguồn cội reviewsachonly\" width=\"1080\" height=\"1080\" srcset=\"https://reviewsach.net/wp-content/uploads/2022/03/Anh-thekibrary_-Nguon-coi-reviewsachonly.jpg 1080w, https://reviewsach.net/wp-content/uploads/2022/03/Anh-thekibrary_-Nguon-coi-reviewsachonly-150x150.jpg 150w, https://reviewsach.net/wp-content/uploads/2022/03/Anh-thekibrary_-Nguon-coi-reviewsachonly-768x768.jpg 768w, https://reviewsach.net/wp-content/uploads/2022/03/Anh-thekibrary_-Nguon-coi-reviewsachonly-300x300.jpg 300w, https://reviewsach.net/wp-content/uploads/2022/03/Anh-thekibrary_-Nguon-coi-reviewsachonly-600x600.jpg 600w, https://reviewsach.net/wp-content/uploads/2022/03/Anh-thekibrary_-Nguon-coi-reviewsachonly-696x696.jpg 696w, https://reviewsach.net/wp-content/uploads/2022/03/Anh-thekibrary_-Nguon-coi-reviewsachonly-1068x1068.jpg 1068w, https://reviewsach.net/wp-content/uploads/2022/03/Anh-thekibrary_-Nguon-coi-reviewsachonly-420x420.jpg 420w, https://reviewsach.net/wp-content/uploads/2022/03/Anh-thekibrary_-Nguon-coi-reviewsachonly-840x840.jpg 840w\" sizes=\"(max-width: 1080px) 100vw, 1080px\" /></noscript><figcaption id=\"caption-attachment-8661\" class=\"wp-caption-text\">Ảnh: thekibrary_</figcaption></figure>\r\n<p style=\"text-align: justify\">Tác phẩm ra mắt độc giả vào tháng 12/2021, do Nhã Nam và NXB Dân Trí liên kết xuất bản, có tên đầy đủ là “Nguồn cội – Chuyện về những người hai quê hương”, nguyên gốc bằng tiếng Anh, dịch giả Trang chuyển ngữ.</p>\r\n<p><strong>Đọc thêm:</strong></p>\r\n<ul>\r\n<li class=\"entry-title\"><a href=\"https://reviewsach.net/tieng-chuong-goi-nguoi-tinh-tro-ve/\" data-wpel-link=\"internal\" rel=\"nofollow noopener noreferrer\">Tiếng chuông gọi người tình trở về – Ngân lên trong vô vọng!</a></li>\r\n<li class=\"entry-title\"><a href=\"https://reviewsach.net/nguoi-ti-nan/\" data-wpel-link=\"internal\" rel=\"nofollow noopener noreferrer\">Người Tị Nạn – Tiếng vọng nơi góc biển.</a></li>\r\n<li class=\"entry-title\"><a href=\"https://reviewsach.net/con-da-ve-nha/\" data-wpel-link=\"internal\" rel=\"nofollow noopener noreferrer\">Con Đã Về Nhà – Ký họa cách ly dịch Covid-19.</a></li>\r\n</ul>\r\n<h2 style=\"text-align: justify\"><strong>Chuyện về những người hai quê hương.</strong></h2>\r\n<p style=\"text-align: justify\">Nguyễn Đan Thy sinh ra và lớn lên tại Sài Gòn, năm 12 tuổi cô rời Việt Nam sang Mỹ định cư, khởi đầu hành trình trưởng thành với nhiều khó khăn khi đứng giữa hai bản sắc văn hoá đầy khác biệt. Những trang viết trong “Nguồn cội” là tiếng nói từ bên trong cộng đồng những người hai quê hương mong muốn được sẻ chia, được lắng nghe, được đồng cảm và được tôn trọng.</p>\r\n<div id=\"at-widget\"></div>\r\n<p style=\"text-align: justify\">“Nguồn cội” gồm 5 phần lớn, chia ra 14 chương nhỏ chưa kể Lời nói đầu và Lời kết. Bố cục cố gắng phác họa toàn bộ quá trình từ lúc chuẩn bị di dân, bắt đầu định cư, những nỗi sợ, những cảm xúc không yên, tự ti, mất kết nối… đến chấp nhận và hòa nhập. Lần lượt qua từng mẩu chuyện, từng bài thơ, Đan Thy ghi lại trải nghiệm của cá nhân và của những người giống mình.</p>\r\n<p style=\"text-align: justify\">Họ phải sống và tập hòa nhập vào môi trường mới khi trong lòng mang nặng phức cảm tự ti, nỗi mặc cảm của kẻ sống bám, của loài ký sinh, vì sự khác biệt từ làn da mái tóc đến ngôn ngữ cùng văn hóa, cho tới ngày họ có thể đứng vững trên đôi chân của mình. Ở đất nước văn minh luôn vỗ ngực tự xưng là “Tự do công lý cho tất cả mọi người” đó vẫn tồn tại đầy rẫy người mang định kiến và phân biệt chủng tộc, đỉnh điểm bài ngoại là khi xuất hiện “loài virus châu Á”. Đối với người bản xứ là một trò đùa, một hành động nhất thời, nhưng đối với những người trong cuộc là từng bước bị đọa đày trong luyện ngục văn hóa.</p>\r\n<figure id=\"attachment_8662\" aria-describedby=\"caption-attachment-8662\" style=\"width: 730px\" class=\"wp-caption alignnone\"><img class=\"size-full wp-image-8662\" src=\"data:image/svg+xml,%3Csvg%20xmlns=\'http://www.w3.org/2000/svg\'%20viewBox=\'0%200%20730%20487\'%3E%3C/svg%3E\" alt=\"Ảnh vovworld Nguồn cội reviewsachonly\" width=\"730\" height=\"487\" data-lazy-srcset=\"https://reviewsach.net/wp-content/uploads/2022/03/Anh-vovworld-Nguon-coi-reviewsachonly.jpg 730w, https://reviewsach.net/wp-content/uploads/2022/03/Anh-vovworld-Nguon-coi-reviewsachonly-150x100.jpg 150w, https://reviewsach.net/wp-content/uploads/2022/03/Anh-vovworld-Nguon-coi-reviewsachonly-300x200.jpg 300w, https://reviewsach.net/wp-content/uploads/2022/03/Anh-vovworld-Nguon-coi-reviewsachonly-600x400.jpg 600w, https://reviewsach.net/wp-content/uploads/2022/03/Anh-vovworld-Nguon-coi-reviewsachonly-696x464.jpg 696w, https://reviewsach.net/wp-content/uploads/2022/03/Anh-vovworld-Nguon-coi-reviewsachonly-630x420.jpg 630w\" data-lazy-sizes=\"(max-width: 730px) 100vw, 730px\" data-lazy-src=\"https://reviewsach.net/wp-content/uploads/2022/03/Anh-vovworld-Nguon-coi-reviewsachonly.jpg\"><noscript><img class=\"size-full wp-image-8662\" src=\"https://reviewsach.net/wp-content/uploads/2022/03/Anh-vovworld-Nguon-coi-reviewsachonly.jpg\" alt=\"Ảnh vovworld Nguồn cội reviewsachonly\" width=\"730\" height=\"487\" srcset=\"https://reviewsach.net/wp-content/uploads/2022/03/Anh-vovworld-Nguon-coi-reviewsachonly.jpg 730w, https://reviewsach.net/wp-content/uploads/2022/03/Anh-vovworld-Nguon-coi-reviewsachonly-150x100.jpg 150w, https://reviewsach.net/wp-content/uploads/2022/03/Anh-vovworld-Nguon-coi-reviewsachonly-300x200.jpg 300w, https://reviewsach.net/wp-content/uploads/2022/03/Anh-vovworld-Nguon-coi-reviewsachonly-600x400.jpg 600w, https://reviewsach.net/wp-content/uploads/2022/03/Anh-vovworld-Nguon-coi-reviewsachonly-696x464.jpg 696w, https://reviewsach.net/wp-content/uploads/2022/03/Anh-vovworld-Nguon-coi-reviewsachonly-630x420.jpg 630w\" sizes=\"(max-width: 730px) 100vw, 730px\" /></noscript><figcaption id=\"caption-attachment-8662\" class=\"wp-caption-text\">Ảnh: vovworld</figcaption></figure>\r\n<p style=\"text-align: justify\">Những nan đề đầy khắc khoải về bản sắc và nhân dạng của người tha hương từng được Giáo sư Viet Thanh Nguyen – nhà văn gốc Việt đầu tiên đoạt giải Pulitzer văn học – khai thác trong tác phẩm “<a href=\"https://reviewsach.net/nguoi-ti-nan/\" data-wpel-link=\"internal\" rel=\"nofollow noopener noreferrer\">Người tị nạn</a>”. Sẽ rất khập khiễng nếu đặt “Nguồn cội” và “Người tị nạn” lên hai đầu cán cân với mục đích so sánh, nhưng dễ dàng khi xếp chúng vào cùng một đề tài văn chương – viết về nhóm người mang trong mình đa dạng căn tính – sẽ thấy mảnh đất này còn sơ khai và cần nhiều hơn những tiếng nói từ bên trong cộng đồng ấy, như Viet Thanh Nguyen, như Thy Nguyen.</p>\r\n<h2 style=\"text-align: justify\"><strong>Tiếng nói của một bản dạng văn hóa linh hoạt.</strong></h2>\r\n<p style=\"text-align: justify\">Toàn cầu hóa xoá mờ ranh giới giữa các quốc gia, làm co hẹp khoảng cách địa lý, đẩy nhanh tốc độ biến đổi về cấu trúc kinh tế – chính trị trong quan hệ liên quốc gia, đa quốc gia, kéo theo những chuyển đổi mạnh mẽ về đời sống văn hoá – xã hội của nhân dân khắp thế giới.</p>\r\n<p style=\"text-align: justify\">Trong thế giới toàn cầu hóa, số lượng công dân toàn cầu – những người sống và làm việc ở nhiều&nbsp;quốc gia&nbsp;khác nhau, có thể có một hoặc nhiều&nbsp;quốc tịch – tăng lên nhanh chóng. Theo Báo cáo Di dân Thế giới năm 2020 từ Tổ chức Di dân Quốc tế của Liên Hợp Quốc (IOM), tỉ lệ di dân quốc tế liên tục tăng trong những năm gần đây, ước đoán con số di dân toàn cầu lên đến 272 triệu. Bản dạng hay bản sắc văn hóa bởi vậy mà ít cứng nhắc đi, dần trở nên linh hoạt hơn.</p>\r\n<p style=\"text-align: justify\">Nhưng để đi từ cứng nhắc đến linh hoạt là cả một quá trình. Và tùy vào mỗi một con người, mỗi một công dân toàn cầu, sẽ có những trải nghiệm, những trăn trở và giằng xé nội tâm rất riêng trong quá trình đó, để đi tìm bản dạng văn hóa của chính mình, để trả lời cho câu hỏi “<em>Bạn là ai? Điều gì làm nên bạn?”</em></p>\r\n<figure id=\"attachment_8660\" aria-describedby=\"caption-attachment-8660\" style=\"width: 1080px\" class=\"wp-caption alignnone\"><img class=\"size-full wp-image-8660\" src=\"data:image/svg+xml,%3Csvg%20xmlns=\'http://www.w3.org/2000/svg\'%20viewBox=\'0%200%201080%20719\'%3E%3C/svg%3E\" alt=\"Ảnh Hiên sách Nguồn cội reviewsachonly\" width=\"1080\" height=\"719\" data-lazy-srcset=\"https://reviewsach.net/wp-content/uploads/2022/03/Anh-Hien-sach-Nguon-coi-reviewsachonly.jpg 1080w, https://reviewsach.net/wp-content/uploads/2022/03/Anh-Hien-sach-Nguon-coi-reviewsachonly-768x511.jpg 768w, https://reviewsach.net/wp-content/uploads/2022/03/Anh-Hien-sach-Nguon-coi-reviewsachonly-150x100.jpg 150w, https://reviewsach.net/wp-content/uploads/2022/03/Anh-Hien-sach-Nguon-coi-reviewsachonly-300x200.jpg 300w, https://reviewsach.net/wp-content/uploads/2022/03/Anh-Hien-sach-Nguon-coi-reviewsachonly-600x399.jpg 600w, https://reviewsach.net/wp-content/uploads/2022/03/Anh-Hien-sach-Nguon-coi-reviewsachonly-696x463.jpg 696w, https://reviewsach.net/wp-content/uploads/2022/03/Anh-Hien-sach-Nguon-coi-reviewsachonly-1068x711.jpg 1068w, https://reviewsach.net/wp-content/uploads/2022/03/Anh-Hien-sach-Nguon-coi-reviewsachonly-631x420.jpg 631w\" data-lazy-sizes=\"(max-width: 1080px) 100vw, 1080px\" data-lazy-src=\"https://reviewsach.net/wp-content/uploads/2022/03/Anh-Hien-sach-Nguon-coi-reviewsachonly.jpg\"><noscript><img class=\"size-full wp-image-8660\" src=\"https://reviewsach.net/wp-content/uploads/2022/03/Anh-Hien-sach-Nguon-coi-reviewsachonly.jpg\" alt=\"Ảnh Hiên sách Nguồn cội reviewsachonly\" width=\"1080\" height=\"719\" srcset=\"https://reviewsach.net/wp-content/uploads/2022/03/Anh-Hien-sach-Nguon-coi-reviewsachonly.jpg 1080w, https://reviewsach.net/wp-content/uploads/2022/03/Anh-Hien-sach-Nguon-coi-reviewsachonly-768x511.jpg 768w, https://reviewsach.net/wp-content/uploads/2022/03/Anh-Hien-sach-Nguon-coi-reviewsachonly-150x100.jpg 150w, https://reviewsach.net/wp-content/uploads/2022/03/Anh-Hien-sach-Nguon-coi-reviewsachonly-300x200.jpg 300w, https://reviewsach.net/wp-content/uploads/2022/03/Anh-Hien-sach-Nguon-coi-reviewsachonly-600x399.jpg 600w, https://reviewsach.net/wp-content/uploads/2022/03/Anh-Hien-sach-Nguon-coi-reviewsachonly-696x463.jpg 696w, https://reviewsach.net/wp-content/uploads/2022/03/Anh-Hien-sach-Nguon-coi-reviewsachonly-1068x711.jpg 1068w, https://reviewsach.net/wp-content/uploads/2022/03/Anh-Hien-sach-Nguon-coi-reviewsachonly-631x420.jpg 631w\" sizes=\"(max-width: 1080px) 100vw, 1080px\" /></noscript><figcaption id=\"caption-attachment-8660\" class=\"wp-caption-text\">Ảnh: Hiên sách</figcaption></figure>\r\n<p style=\"text-align: justify\">“Nguồn cội” của Đan Thy là đôi mắt từ bên trong cộng đồng, là cảm xúc cá nhân tự thể nghiệm nhưng cũng là câu chuyện của những cuộc đời tương đồng khác.</p>\r\n<p style=\"text-align: justify\">Tác phẩm là tiếng nói của một bản dạng văn hóa linh hoạt, đã từng phẫn nộ, đã từng chênh vênh, đã từng lạc lõng… và bây giờ cô đã tìm được bình yên và niềm kiêu hãnh cho bản sắc cá nhân của riêng mình.</p>\r\n<p style=\"text-align: justify\"><em>“Khi người ta hỏi tôi từ đâu đến, tôi nói sự thật, <strong>“Tôi sinh ở Việt Nam rồi chuyển đến đây lúc đang học cấp hai. Vậy nên tôi đến từ cả hai nơi – Sài Gòn và Houston.”</strong> Tôi tự hào vì mình có hai căn tính. Tôi trân trọng sự khác biệt và đa dạng trong mình. Tôi thấy chỉ nhận mình là người Việt hoặc người Mỹ là không đúng. Tôi chẳng là gì cả, đồng thời tôi lại là cả hai. Tôi là sự pha trộn của những mảnh ghép, tôi là tôi.</em></p>\r\n<p style=\"text-align: justify\"><em>Tôi là Nguyễn Đan Thy và tôi cũng là Tee Win, một chút Việt, một chút Mỹ, thoải mái mở bất cứ cánh cửa nào.”</em></p>\r\n<p style=\"text-align: justify\">Xét một cách khách quan, từ nội dung đến ngôn từ, “Nguồn cội” của Nguyễn Đan Thy không quá xuất sắc, cũng không thực sự cuốn hút. Nhưng tác phẩm là tiếng nói thiết tha khơi gợi ý thức về sự giao thoa văn hóa, đồng thời khích lệ mọi người trân trọng và ca tụng sự khác biệt, để mỗi người cảm thấy tự hào về chính màu da của mình. Vì vậy, “Nguồn cội” có lẽ đã hoàn thành sứ mệnh.</p>\r\n<p><strong>Link mua sách:</strong></p>\r\n<ul>\r\n<li>Shopee: <a href=\"https://shorten.asia/fwpvGGhE\" target=\"_blank\" rel=\"noopener sponsored nofollow external noreferrer\" data-wpel-link=\"external\">https://shorten.asia/fwpvGGhE</a></li>\r\n<li>Tiki: <a href=\"https://shorten.asia/XUpA8qjs\" target=\"_blank\" rel=\"noopener sponsored nofollow external noreferrer\" data-wpel-link=\"external\">https://shorten.asia/XUpA8qjs</a></li>\r\n<li>Fahasa/: <a href=\"https://shorten.asia/Nft9eY6F\" target=\"_blank\" rel=\"noopener sponsored nofollow external noreferrer\" data-wpel-link=\"external\">https://shorten.asia/Nft9eY6F</a></li>\r\n</ul>\r\n<div class=\"saboxplugin-wrap\" itemtype=\"http://schema.org/Person\" itemscope=\"\" itemprop=\"author\"><div class=\"saboxplugin-tab\"><div class=\"saboxplugin-gravatar\"><img src=\"data:image/svg+xml,%3Csvg%20xmlns=\'http://www.w3.org/2000/svg\'%20viewBox=\'0%200%20100%20100\'%3E%3C/svg%3E\" width=\"100\" height=\"100\" alt=\"Duyên - reviewsach.net Author\" itemprop=\"image\" data-lazy-src=\"https://reviewsach.net/wp-content/uploads/2020/10/duyen.jpg\"><noscript><img src=\"https://reviewsach.net/wp-content/uploads/2020/10/duyen.jpg\" width=\"100\"  height=\"100\" alt=\"Duyên - reviewsach.net Author\" itemprop=\"image\"></noscript></div><div class=\"saboxplugin-authorname\"><a href=\"https://reviewsach.net/author/duyen/\" class=\"vcard author\" rel=\"author nofollow noopener noreferrer\" itemprop=\"url\" data-wpel-link=\"internal\"><span class=\"fn\" itemprop=\"name\">Duyên Bùi</span></a></div><div class=\"saboxplugin-desc\"><div itemprop=\"description\"><p><span id=\"fbPhotoSnowliftCaption\" class=\"fbPhotosPhotoCaption\" data-ft=\"{\"><span class=\"hasCaption\">A lonely genius said to me: “<em>You know the way. And your curiosity is what sets you apart from the rest. Use your thoughts your hands your hunger for knowledge. Because these things are yours and yours alone.”</em></span></span></p>\r\n<p>That’s the reason why I love reading books.</p>\r\n<div id=\"fbPhotoSnowliftProductMiniListHscroll\" class=\"fbCommerceProductMiniListHscroll\">Tôi là Duyên, yêu văn học và lịch sử, thích Vũ Trọng Phụng.</div>\r\n</div></div><div class=\"clearfix\"></div><div class=\"saboxplugin-socials \"><a target=\"_blank\" href=\"https://www.facebook.com/duyenkoko0810\" aria-label=\"Facebook\" rel=\"noopener nofollow external noreferrer sponsored\" class=\"saboxplugin-icon-grey\" data-wpel-link=\"external\"><svg aria-hidden=\"true\" class=\"sab-facebook\" role=\"img\" xmlns=\"http://www.w3.org/2000/svg\" viewBox=\"0 0 264 512\"><path fill=\"currentColor\" d=\"M76.7 512V283H0v-91h76.7v-71.7C76.7 42.4 124.3 0 193.8 0c33.3 0 61.9 2.5 70.2 3.6V85h-48.2c-37.8 0-45.1 18-45.1 44.3V192H256l-11.7 91h-73.6v229\"></path></svg></a></div></div></div>        </div>\r\n\r\n\r\n        <footer>\r\n                        \r\n            <div class=\"td-post-source-tags\">\r\n                                            </div>\r\n\r\n                        <div class=\"td-block-row td-post-next-prev\"><div class=\"td-block-span6 td-post-prev-post\"><div class=\"td-post-next-prev-content\"><span>Previous article</span><a href=\"https://reviewsach.net/nha-tho-duc-ba-paris/\" data-wpel-link=\"internal\" rel=\"nofollow noopener noreferrer\">Nhà thờ Đức Bà Paris – Khi chân thành vượt qua những khiếm khuyết</a></div></div><div class=\"td-next-prev-separator\"></div><div class=\"td-block-span6 td-post-next-post\"><div class=\"td-post-next-prev-content\"><span>Next article</span><a href=\"https://reviewsach.net/du-lieu-bach-kim/\" data-wpel-link=\"internal\" rel=\"nofollow noopener noreferrer\">Dữ liệu bạch kim (Higashino Keigo) – Cá nhân con người giữa thời số hóa</a></div></div></div>            <div class=\"td-author-name vcard author\" style=\"display: none\"><span class=\"fn\"><a href=\"https://reviewsach.net/author/duyen/\" data-wpel-link=\"internal\" rel=\"nofollow noopener noreferrer\">Duyên Bùi</a></span></div>	        <span class=\"td-page-meta\" itemprop=\"author\" itemscope=\"\" itemtype=\"https://schema.org/Person\"><meta itemprop=\"name\" content=\"Duyên Bùi\"><meta itemprop=\"url\" content=\"https://reviewsach.net/author/duyen/\"></span><meta itemprop=\"datePublished\" content=\"2022-03-18T06:12:27+07:00\"><meta itemprop=\"dateModified\" content=\"2022-05-10T07:12:01+07:00\"><meta itemscope=\"\" itemprop=\"mainEntityOfPage\" itemtype=\"https://schema.org/WebPage\" itemid=\"https://reviewsach.net/nguon-coi-chuyen-ve-nhung-nguoi-hai-que-huong/\"><span class=\"td-page-meta\" itemprop=\"publisher\" itemscope=\"\" itemtype=\"https://schema.org/Organization\"><span class=\"td-page-meta\" itemprop=\"logo\" itemscope=\"\" itemtype=\"https://schema.org/ImageObject\"><meta itemprop=\"url\" content=\"https://reviewsach.net/wp-content/uploads/2021/04/reviewsach.net-logo.png\"></span><meta itemprop=\"name\" content=\"Review sách - Reviewsach.net\"></span><meta itemprop=\"headline \" content=\"Nguồn cội – Tiếng nói của một bản dạng văn hóa linh hoạt.\"><span class=\"td-page-meta\" itemprop=\"image\" itemscope=\"\" itemtype=\"https://schema.org/ImageObject\"><meta itemprop=\"url\" content=\"https://reviewsach.net/wp-content/uploads/2022/03/Featured-Picture-Nguon-coi-reviewsachonly.jpg\"><meta itemprop=\"width\" content=\"533\"><meta itemprop=\"height\" content=\"400\"></span>        </footer>\r\n\r\n    \r\n                                </div>\r\n                            </div>', NULL, NULL, 'Cũng là “Nguồn cội”, nhưng là “Nguồn cội” của Đan Thy chứ không phải của Dan Brown, và hẳn nhiên, đây không phải là một cuốn tiểu thuyết kinh dị bí ẩn. “Nguồn cội” của Nguyễn Đan Thy (Thy Nguyen) là một tuyển tập những truyện ngắn và thơ, viết về văn hóa và bản sắc cá nhân, về bản dạng văn hóa phức tạp đã từng chênh vênh nhưng linh hoạt tìm cách thoát khỏi vùng u minh, để tự tin hòa nhập.	\r\n', NULL),(4, 'Phải trái đúng sai (Michael Sandel): Nhập môn công lí\r\n', 'https://reviewsach.net/wp-content/uploads/2022/02/organ-mua-xuan-kazumi-yumoto-324x235.jpg\r\n', '<div class=\"td-pb-span8 td-main-content\" role=\"main\">\r\n                                <div class=\"td-ss-main-content\">\r\n                                    \r\n        \r\n\r\n        <div class=\"td-post-content tagdiv-type\">\r\n            \r\n            <p style=\"text-align: justify;\">Triết học chưa bao giờ là lĩnh vực dễ dàng. Nhưng triết học là môn khoa học có lẽ gần gũi nhất với đời sống bởi tính ứng dụng cao của nó. Nếu không biết bắt đầu tìm hiểu lĩnh vực này từ đâu, “Phải trái đúng sai” (Nguyên tác: Justice: What’s the right thing to do?”) do giáo sư Harvard Michael Sandel là một sự lựa chọn hoàn hảo.</p>\r\n<figure id=\"attachment_8485\" aria-describedby=\"caption-attachment-8485\" style=\"width: 900px\" class=\"wp-caption alignnone\"><img class=\"size-full wp-image-8485 entered lazyloaded\" src=\"https://reviewsach.net/wp-content/uploads/2022/02/reviewsach.net-phai-trai-dung-sai.jpeg\" alt=\"reviewsach.net phai trai dung sai\" width=\"900\" height=\"900\" data-lazy-srcset=\"https://reviewsach.net/wp-content/uploads/2022/02/reviewsach.net-phai-trai-dung-sai.jpeg 900w, https://reviewsach.net/wp-content/uploads/2022/02/reviewsach.net-phai-trai-dung-sai-150x150.jpeg 150w, https://reviewsach.net/wp-content/uploads/2022/02/reviewsach.net-phai-trai-dung-sai-768x768.jpeg 768w, https://reviewsach.net/wp-content/uploads/2022/02/reviewsach.net-phai-trai-dung-sai-300x300.jpeg 300w, https://reviewsach.net/wp-content/uploads/2022/02/reviewsach.net-phai-trai-dung-sai-600x600.jpeg 600w, https://reviewsach.net/wp-content/uploads/2022/02/reviewsach.net-phai-trai-dung-sai-696x696.jpeg 696w, https://reviewsach.net/wp-content/uploads/2022/02/reviewsach.net-phai-trai-dung-sai-420x420.jpeg 420w, https://reviewsach.net/wp-content/uploads/2022/02/reviewsach.net-phai-trai-dung-sai-840x840.jpeg 840w\" data-lazy-sizes=\"(max-width: 900px) 100vw, 900px\" data-lazy-src=\"https://reviewsach.net/wp-content/uploads/2022/02/reviewsach.net-phai-trai-dung-sai.jpeg\" data-ll-status=\"loaded\" sizes=\"(max-width: 900px) 100vw, 900px\" srcset=\"https://reviewsach.net/wp-content/uploads/2022/02/reviewsach.net-phai-trai-dung-sai.jpeg 900w, https://reviewsach.net/wp-content/uploads/2022/02/reviewsach.net-phai-trai-dung-sai-150x150.jpeg 150w, https://reviewsach.net/wp-content/uploads/2022/02/reviewsach.net-phai-trai-dung-sai-768x768.jpeg 768w, https://reviewsach.net/wp-content/uploads/2022/02/reviewsach.net-phai-trai-dung-sai-300x300.jpeg 300w, https://reviewsach.net/wp-content/uploads/2022/02/reviewsach.net-phai-trai-dung-sai-600x600.jpeg 600w, https://reviewsach.net/wp-content/uploads/2022/02/reviewsach.net-phai-trai-dung-sai-696x696.jpeg 696w, https://reviewsach.net/wp-content/uploads/2022/02/reviewsach.net-phai-trai-dung-sai-420x420.jpeg 420w, https://reviewsach.net/wp-content/uploads/2022/02/reviewsach.net-phai-trai-dung-sai-840x840.jpeg 840w\"><noscript><img class=\"size-full wp-image-8485\" src=\"https://reviewsach.net/wp-content/uploads/2022/02/reviewsach.net-phai-trai-dung-sai.jpeg\" alt=\"reviewsach.net phai trai dung sai\" width=\"900\" height=\"900\" srcset=\"https://reviewsach.net/wp-content/uploads/2022/02/reviewsach.net-phai-trai-dung-sai.jpeg 900w, https://reviewsach.net/wp-content/uploads/2022/02/reviewsach.net-phai-trai-dung-sai-150x150.jpeg 150w, https://reviewsach.net/wp-content/uploads/2022/02/reviewsach.net-phai-trai-dung-sai-768x768.jpeg 768w, https://reviewsach.net/wp-content/uploads/2022/02/reviewsach.net-phai-trai-dung-sai-300x300.jpeg 300w, https://reviewsach.net/wp-content/uploads/2022/02/reviewsach.net-phai-trai-dung-sai-600x600.jpeg 600w, https://reviewsach.net/wp-content/uploads/2022/02/reviewsach.net-phai-trai-dung-sai-696x696.jpeg 696w, https://reviewsach.net/wp-content/uploads/2022/02/reviewsach.net-phai-trai-dung-sai-420x420.jpeg 420w, https://reviewsach.net/wp-content/uploads/2022/02/reviewsach.net-phai-trai-dung-sai-840x840.jpeg 840w\" sizes=\"(max-width: 900px) 100vw, 900px\" /></noscript><figcaption id=\"caption-attachment-8485\" class=\"wp-caption-text\">Ảnh: Instagram @_sa.cafeteria</figcaption></figure>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400;\">Điều hay ở cuốn sách là những chủ nghĩa, học thuyết, tư tưởng khó nhằn nhất trong triết học được tác giả diễn đạt bằng ngôn ngữ vô cùng dễ hiểu, đôi chỗ hài hước nhưng không thiếu tính khoa học, hàm lâm. Cuốn sách không nhằm giải đáp những thắc mắc triết học hay bênh vực, kêu gọi người đọc phải tin theo bất kì quan điểm nào. Thay vào đó, tác giả đánh thức năng lực tư duy, phản biện khoa học một cách đầy thuyết phục và nhân văn; đồng thời gợi mở những băn khoăn triết học mới mẻ khác.&nbsp;</span></p>\r\n<div id=\"at-widget\">\r\n        <style type=\"text/css\">\r\n            .cps-wrap {\r\n                width: 100%;\r\n                margin: 0;\r\n                padding: 0;\r\n                border: 0;\r\n                font: inherit;\r\n                font-size: 100%;\r\n                vertical-align: baseline;\r\n                display: flex;\r\n                flex-wrap: wrap;\r\n            }\r\n\r\n            .cps-widget {\r\n                width: calc(95% / 3);\r\n                position: relative;\r\n                font-family: Open Sans, Helvetica, Arial, sans-serif;\r\n                background: #fff;\r\n                opacity: 1;\r\n                transition: opacity .2s ease-in-out;\r\n                float: left;\r\n                font-size: 14.1px;\r\n                line-height: 21.15px;\r\n                padding: 6px;\r\n            }\r\n\r\n            .cps-widget-border {\r\n                border: 1px solid #d6d6d6;\r\n            }\r\n\r\n            .cps-widget:hover .cps-widget-product-buynow {\r\n                display: inline-block;\r\n            }\r\n\r\n            .cps-widget-img {\r\n                width: 100%;\r\n                position: relative;\r\n          