ortant;\r\n                display: block;\r\n            }\r\n\r\n            .cps-widget-product-buynow a:hover {\r\n                color: #fff !important\r\n            }\r\n\r\n            .cps-widget-product-discount-table {\r\n                position: absolute;\r\n                top: 0;\r\n                right: 0;\r\n                font-size: 13px;\r\n                color: #fff !important;\r\n                background: #4589ca;\r\n                padding: 0 5px;\r\n                border-radius: 5px;\r\n            }\r\n\r\n            .at-cps-row-container {\r\n                border-bottom: 1px dotted #ccc;\r\n                padding: 10px 0;\r\n            }\r\n\r\n            .at-cps-grid-container {\r\n                display: -ms-grid;\r\n                display: grid;\r\n                padding: 5px 10px 0;\r\n                width: 100%;\r\n                box-sizing: border-box;\r\n                position: relative;\r\n                grid-template-columns: 100px repeat(2, 1fr);\r\n            }\r\n\r\n            .at-cps-block-container {\r\n                display: -ms-flexbox;\r\n                display: flex;\r\n                -ms-flex-align: center;\r\n                align-items: center;\r\n                -ms-flex-pack: center;\r\n                justify-content: center;\r\n                -ms-flex-direction: column;\r\n                flex-direction: column;\r\n            }\r\n\r\n            .at-cps-price-container {\r\n                display: -ms-flexbox;\r\n                display: flex;\r\n                -ms-flex-pack: center;\r\n                justify-content: center;\r\n                -ms-flex-direction: column;\r\n                flex-direction: column;\r\n                -ms-flex-align: center;\r\n                align-items: center;\r\n            }\r\n\r\n            .at-cps-block-text-container {\r\n                display: -ms-flexbox;\r\n                display: flex;\r\n                -ms-flex-pack: center;\r\n    