n                transition: all 0.3s ease 0s;\r\n                text-align: center;\r\n            }\r\n\r\n            .at-cps-aff-button:hover {\r\n                background-image: linear-gradient(rgba(0, 0, 0, 0.2), rgba(0, 0, 0, 0.2));\r\n                color: #ffffff;\r\n            }\r\n\r\n            .at-cps-image-container {\r\n                display: -ms-flexbox;\r\n                display: flex;\r\n                -ms-flex-align: center;\r\n                align-items: center;\r\n                -ms-flex-pack: center;\r\n                justify-content: center;\r\n                height: 75px;\r\n            }\r\n\r\n            .at-cps-text {\r\n                color: #333;\r\n                text-align: left;\r\n            }\r\n\r\n            .at-cps-text span {\r\n                color: #888;\r\n                font-weight: 400;\r\n            }\r\n\r\n            .at-sps-text-line {\r\n                color: #848484;\r\n                text-decoration: line-through;\r\n                font-size: 13px;\r\n                font-weight: 400;\r\n            }\r\n\r\n            .at-cps-align-item {\r\n                align-items: flex-start;\r\n                padding-left: 5px !important;\r\n            }\r\n\r\n            .at-cps-aff-button-main a:active,\r\n            .at-cps-aff-button-main a:focus {\r\n                color: #ffffff;\r\n            }\r\n\r\n            .at-cps-relative {\r\n                position: relative;\r\n            }\r\n\r\n            .at-cps-sup {\r\n                position: relative;\r\n                font-size: 75%;\r\n                line-height: 0;\r\n                vertical-align: baseline;\r\n                top: -5px;\r\n            }\r\n\r\n            .at-cps-btn {\r\n                display: inline-block;\r\n                color: #fff !important;\r\n                text-decoration: none;\r\n                font-size: 13px;\r\n                font-weight: 700;\r\n                background-color: #4589ca !important;\r\n                word-break: normal;\r\n                letter-spacing: normal;\r\n                border-radius: 0px;\r\n                border: none;\r\n                transition: all 0.3s ease 0s;\r\n                text-align: center;\r\n                padding: 7px 14px;\r\n                margin-bottom: 0;\r\n                white-space: nowrap;\r\n                vertical-align: middle;\r\n            }\r\n\r\n            .at-cps-btn:hover {\r\n                background-image: linear-gradient(rgba(0, 0, 0, 0.2), rgba(0, 0, 0, 0.2));\r\n                color: #ffffff;\r\n            }\r\n\r\n            @media  screen and (max-width: 767px) {\r\n                .cps-widget {\r\n                    width: 50%;\r\n                }\r\n            }\r\n            /*type 3 ---------------------------------------------------------------------*/\r\n        .at-cps-3{\r\n        display: flex;\r\n    }\r\n    .at-cps-3>a{\r\n        flex: 0 0 35%;\r\n        margin-right: 10px;\r\n    }\r\n    .at-cps-3>a>img{\r\n        display: block;\r\n        width: auto;\r\n        height: auto !important;\r\n        margin: auto;\r\n        max-width: 100%;\r\n        max-height: 200px;\r\n    }\r\n    .at-cps-3>div{\r\n        flex: 1;\r\n    }\r\n    .at-cps-3-item{\r\n        display: flex;\r\n        margin-bottom: 15px;\r\n        align-items: center;\r\n    }\r\n    .at-cps-3-flex-1{\r\n        flex: 1;\r\n    }\r\n    .at-cps-3-item .at-price {\r\n        font-weight: 600;\r\n        font-size: 16px;\r\n        color: #00695C;\r\n    }\r\n    .at-cps-3-title{\r\n        margin-bottom: 10px;\r\n        font-weight: 500;\r\n        letter-spacing: -0.02em;\r\n        line-height: 1.2;\r\n        -webkit-font-smoothing: antialiased;\r\n        -moz-osx-font-smoothing: grayscale;\r\n        font-size: 18px;\r\n        overflow: hidden;\r\n        display: -webkit-box;\r\n        text-overflow: ellipsis;\r\n        -webkit-box-orient: vertical;\r\n        -webkit-line-clamp: 3;\r\n    }\r\n    .at-cps-3-shop{\r\n        font-weight: 500;\r\n        color: #4589ca !important;\r\n        text-decoration: none;\r\n    }\r\n    .at-cps-3-flex-1:nth-child(3){\r\n        text-align: right;\r\n    }\r\n    @media  screen and (max-width: 767px){\r\n        .at-cps-3 {\r\n            display: block;\r\n            padding: 0 10px;\r\n        }\r\n        .at-cps-3-title{\r\n            text-align: center;\r\n            margin-top: 10px;\r\n        }\r\n        .at-cps-3>a{\r\n            margin-right: 0;\r\n        }\r\n    }\r\n\r\n            </style> <div class=\"cps-wrap\"> <div class=\"at-cps-3\">\r\n    <a target=\"_blank\" href=\"https://shorten.asia/RwzetQNY\" rel=\"nofollow\">\r\n        <img src=\"https://salt.tikicdn.com/cache/280x280/ts/product/e5/cd/f3/7f2dfcf796d203b9870bfadbe7c16313.jpg\" alt=\"Phải Trái Đúng Sai (Tái Bản 2017)\">\r\n    </a>\r\n    <div>\r\n        <p class=\"at-cps-3-title\">Phải Trái Đúng Sai (Tái Bản 2017)</p>\r\n        <div class=\"at-cps-3-item\">\r\n            <div class=\"at-cps-3-flex-1\">\r\n                <a class=\"at-cps-3-shop\" rel=\"nofollow\" target=\"_blank\" href=\" https://shorten.asia/RwzetQNY\">  tiki </a>\r\n            </div>\r\n            <div class=\"at-cps-3-flex-1\">\r\n                <div class=\"mo-price-row\">\r\n                    <div class=\"mo-price\">105,000<span class=\"mo-cps-sup\">đ</span></div>\r\n                </div>\r\n            </div>\r\n            <div class=\"at-cps-3-flex-1\">\r\n                <a target=\"_blank\" href=\"  https://shorten.asia/RwzetQNY \" class=\"at-cps-btn\" rel=\"nofollow\"><span> Mua Ngay </span></a>\r\n            </div>\r\n        </div><div class=\"at-cps-3-item\">\r\n            <div class=\"at-cps-3-flex-1\">\r\n                <a class=\"at-cps-3-shop\" rel=\"nofollow\" target=\"_blank\" href=\" https://shorten.asia/xcgggzKz\">  shopee </a>\r\n            </div>\r\n            <div class=\"at-cps-3-flex-1\">\r\n                <div class=\"mo-price-row\">\r\n                    <div class=\"mo-price\">112,000.0<span class=\"mo-cps-sup\">đ</span></div>\r\n                </div>\r\n            </div>\r\n            <div class=\"at-cps-3-flex-1\">\r\n                <a target=\"_blank\" href=\"  https://shorten.asia/xcgggzKz \" class=\"at-cps-btn\" rel=\"nofollow\"><span> Mua Ngay </span></a>\r\n            </div>\r\n        </div>\r\n    </div>\r\n</div> </div></div>\r\n<p><script data-minify=\"1\" async=\"\" type=\"application/javascript\" src=\"https://reviewsach.net/wp-content/cache/min/1/pub/files/0716565709c9d9391e4ca32714af5434.js?ver=1660403945\"></script></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400;\">Những chủ nghĩa, học thuyết, quan điểm triết học với những cái tên làm khuynh đảo nền triết học suốt hàng năm lịch sử cùng những vấn đề muôn thuở và nổi cộm làm nên một cuốn sách hấp dẫn, làm thay đổi người đọc.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400;\">Cuốn sách bao gồm 10 chương, tương đương với 10 câu hỏi lớn – 10 vấn đề nhức nhối của cuộc sống cùng với hàng loạt những triết gia, những câu chuyện người thật việc thật đầy hấp dẫn và suy tư.&nbsp;</span></p>\r\n<p style=\"text-align: justify;\">Chương 1: Làm việc đúng</p>\r\n<p style=\"text-align: justify;\">Chương 2: Nguyên tắc hạnh phúc cực đại – Thuyết vị lợi</p>\r\n<p style=\"text-align: justify;\">Chương 3: Chúng ta có sở hữu chính mình không?/ Chủ nghĩa tự do cá nhân</p>\r\n<p style=\"text-align: justify;\">Chương 4: Thuê trợ giúp – Thị trường và đạo đức</p>\r\n<p style=\"text-align: justify;\">Chương 5: Động cơ mới quan trọng – Immanuel Kant</p>\r\n<p style=\"text-align: justify;\">Chương 6: Lý lẽ bình đẳng – John Rawls</p>\r\n<p style=\"text-align: justify;\">Chương 7: Tranh cãi về chính sách chống kì thị</p>\r\n<p style=\"text-align: justify;\">Chương 8: Ai xứng đáng với thứ gì? – Aristotles</p>\r\n<p style=\"text-align: justify;\">Chương 9: Chúng ta nợ người khác những gì – Lòng trung thành khó xử</p>\r\n<p style=\"text-align: justify;\">Chương 10: Công lí và lợi ích chung</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400;\">Cuốn sách mở đầu Chương 1 bằng ví dụ đầy cấp thiết đến cuộc sống như việc tăng giá kinh hoàng trong thời điểm thiên tai tại miền Đông nước Mỹ năm 2004. Ý tưởng về tối đa hóa phúc lợi, tôn trọng tự do và đề cao đạo đức được đưa ra để mổ xẻ, phân tích. Liệu có thể nào vin vào ý tưởng về thị trường tự do, bất chấp những quan điểm về đạo đức, cách đối nhân xử thế để ủng hộ hành vi bán phá giá?&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400;\">Chương 1 tiếp tục bằng những băn khoăn đầy chính đáng trong việc trao tặng huân chương Tử tâm cho những binh sĩ bị thương hoặc thiệt mạng trong chiến trận hay nỗi căm phẫn từ việc chính phủ Mỹ mở gói cứu trợ cho ngân hàng, công ty tài chính lớn của quốc gia – vốn là những đối tượng bị đổ tội cho cuộc khủng hoảng kinh tế năm 2008-2009.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400;\">Thông qua những băn khoăn sơ khởi nhằm thu hút người đọc, tác giả Michael Sandel kết lại bằng “ba phương pháp tiếp cận công lí”: Thứ nhất, bắt đầu với ý tưởng về tối đa hóa phúc lợi. Điển hình khi tìm hiểu ý tưởng này là thuyết vị lợi, học thuyết lí giải sâu sắc tại sao và làm cách nào để tối đa hóa phúc lợi. Tiếp theo, cuốn sách xét một loạt học thuyết kết nối công lí và tự do. Và cuối cùng là xét học thuyết coi công lí gắn với đạo đức và lối sống tốt đẹp.&nbsp;</span></p>\r\n<p style=\"text-align: justify;\">Lấy ví dụ ở Chương 2: Nguyên tắc hạnh phúc cực đại – Thuyết vị lợi</p>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400;\">Chương 2 mở ra trước mắt người đọc một trong những chủ nghĩa có lẽ là nổi tiếng và sức ảnh hưởng lớn nhất trong nền triết học cận đại và hiện đại – Chủ nghĩa vị lợi của Jeremy Bentham (1748-1832). Mở đầu chương là một câu chuyện có thật về bốn thủy thủ còn sống sót trên một con thuyền cứu trợ giữa Nam Đại Tây Dương năm 1884. Trong đó có ba người khỏe mạnh là: thuyền trưởng, thủy thủ trưởng, thuyền viên; và một cậu bé thử việc đang kiệt sức và đau ốm. Giữa cơn đói khát do cạn kiệt nguồn thức ăn trên thuyền, thuyền trưởng, thủy thủ trưởng lựa chọn giết chết cậu bé thử việc để sống sót, thuyền viên không tham gia vào việc đó nhưng cũng “thưởng thức” bữa ăn kinh hoàng và cả ba cùng sống sót. Ba người sau đó được cứu sống và ra hầu tòa.&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400;\">Câu hỏi đặt ra là, nếu là thẩm phán trong vụ án này, bạn sẽ phán quyết thế nào?&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400;\">Tác giả đưa ra những lập luận ủng hộ lẫn phản đối hành vi giết người ăn thịt trên, nhưng hoàn toàn không nương theo bất kì ý kiến nào. Tất cả những lập luận là nhằm khơi gợi đến quan điểm của chủ nghĩa vị lợi: </span><i><span style=\"font-weight: 400;\">“Nguyên tắc đạo đức cao nhất là tối đa hóa hạnh phúc, trong mối cân bằng tổng thể giữa hạnh phúc và đau khổ.” </span></i><span style=\"font-weight: 400;\">Lí luận quan điểm này, Bentham cho rằng, cảm xúc đau khổ hay hạnh phúc là “chủ nhân tuyệt đối” của mỗi người, chi phối con người ở mọi mặt của cuộc sống. Quan điểm và lập luận như vậy rất dễ được đồng thuận, do rất dễ hiểu, dể cảm. Tác giả đưa thêm những ví dụ để làm rõ sự hợp lí của chủ nghĩa vị lợi như việc quản lí người ăn xin của Bentham.</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400;\">Tuy thế, Michael Sandel đưa ra những lập luận đầy chặt chẽ để phản đối quan điểm của Bentham, đánh thẳng vào những lỗ hổng “chí mạng” của chủ nghĩa vị lợi. Phản đối thứ nhất là chủ nghĩa vị lợi không tôn trọng quyền cá nhân. Phản đối thứ 2 là việc không thể áp dụng một đồng tiên chung do giá trị hạnh phúc và khổ đau. Từng lập luận phản đối đều dẫn chiếu những ví dụ thực tiễn đầy thuyết phục và hấp dẫn.&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400;\">Nhưng mọi việc không dừng ở đó, một triết gia nổi tiếng tin rằng có thể bảo vệ quan điểm của chủ nghĩa vị lợi là John Stuart Mill. Lập luận của Mill có vẻ chặt chẽ hơn tiền nhân Bentham ở chỗ, ông cố gắng tương thích quyền cá nhân với triết thuyết vị lợi. Mill cho rằng “người dân phải được tự do làm bất cứ điều gì họ muốn, miễn là điều này không gây hại cho người khác”, và nên tối đa hóa lợi ích về lâu về dài. Tôn trọng tự do cá nhân mang lại lợi ích về lâu về dài, mang lại hạnh phúc lớn lao nhất cho loài người.&nbsp;</span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400;\">Tranh luận vẫn tiếp tục, trong sự giằng co của các bên để bảo vệ quan điểm của chính mình, người đọc lại càng vỡ ra trong đầu những điều mới mẻ mà trước đây chưa từng nghĩ đến. Điều quan trọng là chúng ta không dừng lại ở việc thỏa mãn và nương theo bất kì quan điểm nào, mà luôn luôn nghi hoặc, lật đi lật lại vấn đề. Trong những chương sau, những câu hỏi cốt lõi nhất của triết học lại được lật mở. Ví dụ như ở chương 3, liệu việc đánh thuế cao những người giàu có công bằng hay không? Chúng ta có sở hữu chính mình hay không? Hay ở chương 5, về một trong những triết gia có ảnh hưởng nhất triết học cận đại – Immanuel Kant, ông nghĩ gì về tự do? Ở chương 6: thế nào là bình đẳng? Chương 8: Ai xứng đáng với thứ gì? Quan điểm của Aristotle cho rằng người giỏi nhất xứng đáng với tài nguyên tốt nhất có hợp lí hay không?&nbsp;</span></p>\r\n<figure id=\"attachment_8486\" aria-describedby=\"caption-attachment-8486\" style=\"width: 480px\" class=\"wp-caption aligncenter\"><img class=\"size-full wp-image-8486\" src=\"data:image/svg+xml,%3Csvg%20xmlns=\'http://www.w3.org/2000/svg\'%20viewBox=\'0%200%20480%20600\'%3E%3C/svg%3E\" alt=\"review sach triet hoc phai trai dung sai by reviewsach.net\" width=\"480\" height=\"600\" data-lazy-srcset=\"https://reviewsach.net/wp-content/uploads/2022/02/review-sach-triet-hoc-phai-trai-dung-sai-by-reviewsach.net_.jpeg 480w, https://reviewsach.net/wp-content/uploads/2022/02/review-sach-triet-hoc-phai-trai-dung-sai-by-reviewsach.net_-150x188.jpeg 150w, https://reviewsach.net/wp-content/uploads/2022/02/review-sach-triet-hoc-phai-trai-dung-sai-by-reviewsach.net_-300x375.jpeg 300w, https://reviewsach.net/wp-content/uploads/2022/02/review-sach-triet-hoc-phai-trai-dung-sai-by-reviewsach.net_-336x420.jpeg 336w\" data-lazy-sizes=\"(max-width: 480px) 100vw, 480px\" data-lazy-src=\"https://reviewsach.net/wp-content/uploads/2022/02/review-sach-triet-hoc-phai-trai-dung-sai-by-reviewsach.net_.jpeg\"><noscript><img class=\"size-full wp-image-8486\" src=\"https://reviewsach.net/wp-content/uploads/2022/02/review-sach-triet-hoc-phai-trai-dung-sai-by-reviewsach.net_.jpeg\" alt=\"review sach triet hoc phai trai dung sai by reviewsach.net\" width=\"480\" height=\"600\" srcset=\"https://reviewsach.net/wp-content/uploads/2022/02/review-sach-triet-hoc-phai-trai-dung-sai-by-reviewsach.net_.jpeg 480w, https://reviewsach.net/wp-content/uploads/2022/02/review-sach-triet-hoc-phai-trai-dung-sai-by-reviewsach.net_-150x188.jpeg 150w, https://reviewsach.net/wp-content/uploads/2022/02/review-sach-triet-hoc-phai-trai-dung-sai-by-reviewsach.net_-300x375.jpeg 300w, https://reviewsach.net/wp-content/uploads/2022/02/review-sach-triet-hoc-phai-trai-dung-sai-by-reviewsach.net_-336x420.jpeg 336w\" sizes=\"(max-width: 480px) 100vw, 480px\" /></noscript><figcaption id=\"caption-attachment-8486\" class=\"wp-caption-text\">Ảnh: @Instagram hanguyenph</figcaption></figure>\r\n<p style=\"text-align: justify;\"><span style=\"font-weight: 400;\">Và cứ như thế, những vấn đề đầy tranh cãi được lần giở thông qua những ví dụ đầy sinh động và thực tế. Điều đặc biệt là cuốn sách cùng với </span><span style=\"font-weight: 400;\">bài giảng</span><span style=\"font-weight: 400;\"> của Michael Sandel được trình bày theo phương pháp giảng dạy rất văn minh và hấp dẫn của một trong những trường đại học hàng đầu của Mỹ – Harvard, nên cách trình bày cũng rất khác so với những tài liệu hay bài giảng của các nước theo giáo dục xã hội chủ nghĩa, ví dụ như Việt Nam. Nếu bạn đã quen với việc giảng viên hoặc người viết sách nêu các khái niệm khô khan trước sau đó mới đến những ví dụ thực tiễn thì cuốn sách và bài giảng của Michael lại làm ngược lại: Khơi gợi người đọc qua những ví dụ thực tiễn hoặc giả định, vừa mang ý nghĩa triết học, vừa khuyến khích tranh luận giữa các quan niệm rồi sau đó với soi chiếu vào những lí thuyết cốt lõi nhất của triết học.&nbsp;</span></p>\r\n<p><strong>Link mua sách:</strong></p>\r\n<ul>\r\n<li>Tiki: <a href=\"https://shorten.asia/SGCmu4Ke\" target=\"_blank\" rel=\"noopener sponsored nofollow external noreferrer\" data-wpel-link=\"external\">https://shorten.asia/SGCmu4Ke</a></li>\r\n<li>Shopee: <a href=\"https://shorten.asia/uVgyt9TQ\" target=\"_blank\" rel=\"noopener sponsored nofollow external noreferrer\" data-wpel-link=\"external\">https://shorten.asia/uVgyt9TQ</a></li>\r\n<li>Fahasa: <a href=\"https://shorten.asia/uVgyt9TQ\" target=\"_blank\" rel=\"noopener sponsored nofollow external noreferrer\" data-wpel-link=\"external\">https://shorten.asia/uVgyt9TQ</a></li>\r\n<li>Lazada: <a href=\"https://shorten.asia/szbDSxzp\" target=\"_blank\" rel=\"noopener sponsored nofollow external noreferrer\" data-wpel-link=\"external\">https://shorten.asia/szbDSxzp</a></li>\r\n</ul>\r\n<div class=\"saboxplugin-wrap\" itemtype=\"http://schema.org/Person\" itemscope=\"\" itemprop=\"author\"><div class=\"saboxplugin-tab\"><div class=\"saboxplugin-gravatar\"><img src=\"data:image/svg+xml,%3Csvg%20xmlns=\'http://www.w3.org/2000/svg\'%20viewBox=\'0%200%20100%20100\'%3E%3C/svg%3E\" width=\"100\" height=\"100\" alt=\"Bà Gấu\" itemprop=\"image\" data-lazy-src=\"https://reviewsach.net/wp-content/uploads/2018/12/Gấu.jpg\"><noscript><img src=\"https://reviewsach.net/wp-content/uploads/2018/12/Gấu.jpg\" width=\"100\"  height=\"100\" alt=\"Bà Gấu\" itemprop=\"image\"></noscript></div><div class=\"saboxplugin-authorname\"><a href=\"https://reviewsach.net/author/gaugau/\" class=\"vcard author\" rel=\"author nofollow noopener noreferrer\" itemprop=\"url\" data-wpel-link=\"internal\"><span class=\"fn\" itemprop=\"name\">Gấu Mèo</span></a></div><div class=\"saboxplugin-desc\"><div itemprop=\"description\"><p>Gấu là một tác giả lặng thầm dễ thương nhất, không yêu cầu gì nhiều ngoài chút nhuận bút cỏn con đủ để uống một ly sinh tố giữa trưa hè nóng bức</p>\r\n<p>Đùa thôi, nói vậy chứ Gấu Mèo phụ trách nhiều chuyên mục lắm đấy nhé</p>\r\n</div></div><div class=\"clearfix\"></div></div></div>        </div>\r\n\r\n\r\n        <footer>\r\n                        \r\n            <div class=\"td-post-source-tags\">\r\n                                            </div>\r\n\r\n                        <div class=\"td-block-row td-post-next-prev\"><div class=\"td-block-span6 td-post-prev-post\"><div class=\"td-post-next-prev-content\"><span>Previous article</span><a href=\"https://reviewsach.net/lieu-trai-chi-di/\" data-wpel-link=\"internal\" rel=\"nofollow noopener noreferrer\">Liêu trai chí dị – Dạo chơi trần thế bàn chuyện nhân sinh</a></div></div><div class=\"td-next-prev-separator\"></div><div class=\"td-block-span6 td-post-next-post\"><div class=\"td-post-next-prev-content\"><span>Next article</span><a href=\"https://reviewsach.net/neu-dem-dong-co-nguoi-lu-khach/\" data-wpel-link=\"internal\" rel=\"nofollow noopener noreferrer\">Nếu đêm đông có người lữ khách: Khi một cuốn sách mở ra</a></div></div></div>            <div class=\"td-author-name vcard author\" style=\"display: none\"><span class=\"fn\"><a href=\"https://reviewsach.net/author/gaugau/\" data-wpel-link=\"internal\" rel=\"nofollow noopener noreferrer\">Gấu Mèo</a></span></div>	        <span class=\"td-page-meta\" itemprop=\"author\" itemscope=\"\" itemtype=\"https://schema.org/Person\"><meta itemprop=\"name\" content=\"Gấu Mèo\"><meta itemprop=\"url\" content=\"https://reviewsach.net/author/gaugau/\"></span><meta itemprop=\"datePublished\" content=\"2022-02-16T11:04:01+07:00\"><meta itemprop=\"dateModified\" content=\"2022-02-17T15:16:58+07:00\"><meta itemscope=\"\" itemprop=\"mainEntityOfPage\" itemtype=\"https://schema.org/WebPage\" itemid=\"https://reviewsach.net/phai-trai-dung-sai/\"><span class=\"td-page-meta\" itemprop=\"publisher\" itemscope=\"\" itemtype=\"https://schema.org/Organization\"><span class=\"td-page-meta\" itemprop=\"logo\" itemscope=\"\" itemtype=\"https://schema.org/ImageObject\"><meta itemprop=\"url\" content=\"https://reviewsach.net/wp-content/uploads/2021/04/reviewsach.net-logo.png\"></span><meta itemprop=\"name\" content=\"Review sách - Reviewsach.net\"></span><meta itemprop=\"headline \" content=\"Phải trái đúng sai (Michael Sandel): Nhập môn công lí\"><span class=\"td-page-meta\" itemprop=\"image\" itemscope=\"\" itemtype=\"https://schema.org/ImageObject\"><meta itemprop=\"url\" content=\"https://reviewsach.net/wp-content/uploads/2022/02/cover-sach-phai-trai-dung-sai.jpeg\"><meta itemprop=\"width\" content=\"533\"><meta itemprop=\"height\" content=\"399\"></span>        </footer>\r\n\r\n    \r\n                                </div>\r\n                            </div>', NULL, NULL, 'Triết học chưa bao giờ là lĩnh vực dễ dàng. Nhưng triết học là môn khoa học có lẽ gần gũi nhất với đời sống bởi tính ứng dụng cao của nó. Nếu không biết bắt đầu tìm hiểu lĩnh vực này từ đâu, “Phải trái đúng sai” (Nguyên tác: Justice: What’s the right thing to do?”) do giáo sư Harvard Michael Sandel là một sự lựa chọn hoàn hảo.\r\n', NULL),(5, 'Điều bí mật (Yoru Sumino) – Ai cũng cần một khoảng lặng cho tâm hồn\r\n', 'https://reviewsach.net/wp-content/uploads/2022/01/dieu-bi-mat-yoru-sumino-review-324x235.jpg', '<div class=\"td-pb-span8 td-main-content\" role=\"main\">\r\n                                <div class=\"td-ss-main-content\">\r\n                                    \r\n        \r\n\r\n        <div class=\"td-post-content tagdiv-type\">\r\n            \r\n            <h5 style=\"text-align: justify;\"><em><i><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">Group you year people, ai cũng mang đặc điểm riêng có thể nhìn nội tâm, cảm xúc đối phương. </span></span></span><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">Nhưng phải đến khi trải qua quãng thời gian thật sự bên nhau, mỗi người mới thực sự nhận ra, không có năng lực nào giúp hiểu rõ tâm tư, tình cảm của mỗi người. </span></span></span><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">Và rằng, ai cũng có một khoảng lặng cho tâm hồn ẩn chứa bí mật khó có thể chia sẻ. </span></span></span><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">Vì thế, tôn trọng bí mật, cảm xúc của bạn và sống chân thành với cảm xúc của chính mình, cũng là cách, tôn trọng cơ bản và cho một tình yêu bạn đẹp.</span></span></span></span></i></em></h5>\r\n<p style=\"text-align: justify;\"><em><i><img class=\"size-full wp-image-8420 aligncenter entered lazyloaded\" src=\"https://reviewsach.net/wp-content/uploads/2022/01/dieu-bi-mat-yoru-sumino-1.jpg\" alt=\"yoru sumino bí mật\" width=\"960\" height=\"720\" data-lazy-srcset=\"https://reviewsach.net/wp-content/uploads/2022/01/dieu-bi-mat-yoru-sumino-1.jpg 960w, https://reviewsach.net/wp-content/uploads/2022/01/dieu-bi-mat-yoru-sumino-1-768x576.jpg 768w, https://reviewsach.net/wp-content/uploads/2022/01/dieu-bi-mat-yoru-sumino-1-150x113.jpg 150w, https://reviewsach.net/wp-content/uploads/2022/01/dieu-bi-mat-yoru-sumino-1-300x225.jpg 300w, https://reviewsach.net/wp-content/uploads/2022/01/dieu-bi-mat-yoru-sumino-1-600x450.jpg 600w, https://reviewsach.net/wp-content/uploads/2022/01/dieu-bi-mat-yoru-sumino-1-696x522.jpg 696w, https://reviewsach.net/wp-content/uploads/2022/01/dieu-bi-mat-yoru-sumino-1-560x420.jpg 560w, https://reviewsach.net/wp-content/uploads/2022/01/dieu-bi-mat-yoru-sumino-1-80x60.jpg 80w, https://reviewsach.net/wp-content/uploads/2022/01/dieu-bi-mat-yoru-sumino-1-160x120.jpg 160w, https://reviewsach.net/wp-content/uploads/2022/01/dieu-bi-mat-yoru-sumino-1-265x198.jpg 265w\" data-lazy-sizes=\"(max-width: 960px) 100vw, 960px\" data-lazy-src=\"https://reviewsach.net/wp-content/uploads/2022/01/dieu-bi-mat-yoru-sumino-1.jpg\" data-ll-status=\"loaded\" sizes=\"(max-width: 960px) 100vw, 960px\" srcset=\"https://reviewsach.net/wp-content/uploads/2022/01/dieu-bi-mat-yoru-sumino-1.jpg 960w, https://reviewsach.net/wp-content/uploads/2022/01/dieu-bi-mat-yoru-sumino-1-768x576.jpg 768w, https://reviewsach.net/wp-content/uploads/2022/01/dieu-bi-mat-yoru-sumino-1-150x113.jpg 150w, https://reviewsach.net/wp-content/uploads/2022/01/dieu-bi-mat-yoru-sumino-1-300x225.jpg 300w, https://reviewsach.net/wp-content/uploads/2022/01/dieu-bi-mat-yoru-sumino-1-600x450.jpg 600w, https://reviewsach.net/wp-content/uploads/2022/01/dieu-bi-mat-yoru-sumino-1-696x522.jpg 696w, https://reviewsach.net/wp-content/uploads/2022/01/dieu-bi-mat-yoru-sumino-1-560x420.jpg 560w, https://reviewsach.net/wp-content/uploads/2022/01/dieu-bi-mat-yoru-sumino-1-80x60.jpg 80w, https://reviewsach.net/wp-content/uploads/2022/01/dieu-bi-mat-yoru-sumino-1-160x120.jpg 160w, https://reviewsach.net/wp-content/uploads/2022/01/dieu-bi-mat-yoru-sumino-1-265x198.jpg 265w\"><noscript><img class=\"size-full wp-image-8420 aligncenter\" src=\"https://reviewsach.net/wp-content/uploads/2022/01/dieu-bi-mat-yoru-sumino-1.jpg\" alt=\"yoru sumino bí mật\" width=\"960\" height=\"720\" srcset=\"https://reviewsach.net/wp-content/uploads/2022/01/dieu-bi-mat-yoru-sumino-1.jpg 960w, https://reviewsach.net/wp-content/uploads/2022/01/dieu-bi-mat-yoru-sumino-1-768x576.jpg 768w, https://reviewsach.net/wp-content/uploads/2022/01/dieu-bi-mat-yoru-sumino-1-150x113.jpg 150w, https://reviewsach.net/wp-content/uploads/2022/01/dieu-bi-mat-yoru-sumino-1-300x225.jpg 300w, https://reviewsach.net/wp-content/uploads/2022/01/dieu-bi-mat-yoru-sumino-1-600x450.jpg 600w, https://reviewsach.net/wp-content/uploads/2022/01/dieu-bi-mat-yoru-sumino-1-696x522.jpg 696w, https://reviewsach.net/wp-content/uploads/2022/01/dieu-bi-mat-yoru-sumino-1-560x420.jpg 560w, https://reviewsach.net/wp-content/uploads/2022/01/dieu-bi-mat-yoru-sumino-1-80x60.jpg 80w, https://reviewsach.net/wp-content/uploads/2022/01/dieu-bi-mat-yoru-sumino-1-160x120.jpg 160w, https://reviewsach.net/wp-content/uploads/2022/01/dieu-bi-mat-yoru-sumino-1-265x198.jpg 265w\" sizes=\"(max-width: 960px) 100vw, 960px\" /></noscript></i></em></p>\r\n<h5 style=\"text-align: justify;\"><strong><b><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">Câu chuyện được kể từ năm nhìn</span></span></span></span></b></strong></h5>\r\n<p style=\"text-align: justify;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">Là một tác giả luôn tìm kiếm, thay đổi mới phương thức, trong mỗi tiểu thuyết, Yoru Sumino trở lại đưa ra độc giả vào một thế giới truyện với các phương thức hết sức khác nhau. </span></span></span><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">Khi câu chuyện được kể từ ngôi thứ nhất, những người kể chuyện “tôi”, kể về khoảng thời gian bên cạnh các bạn cùng lớp như </span></span></span></span><em><i><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">Tôi muốn ăn thịt cậu</span></span></span></span></i></em><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"> &nbsp;hay </span></span></span></span><a href=\"https://reviewsach.net/quai-vat-trong-dem/\" data-wpel-link=\"internal\" rel=\"nofollow noopener noreferrer\"><em><i><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">chơi trong đêm</span></span></span></span></i></em></a><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"> ; </span></span></span><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">Khi nào thì tác giả được xếp lại từ ngôi thứ ba, người đứng ở góc nhìn toàn cảnh, đặt vào cuộc sống thường nhật của một cô gái trưởng thành trong </span></span></span></span><em><i><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\">Mong bạn sẽ trở thành một người thú vị</span></span></span></span></i></em><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"><span style=\"vertical-align: inherit;\"> .</span></span></span></span></p>\r\n<div id=\"at-widget\">\r\n        <style type=\"text/css\">\r\n            .cps-wrap {\r\n                width: 100%;\r\n                margin: 0;\r\n                padding: 0;\r\n                border: 0;\r\n                font: inherit;\r\n                font-size: 100%;\r\n                vertical-align: baseline;\r\n                display: flex;\r\n                flex-wrap: wrap;\r\n            }\r\n\r\n            .cps-widget {\r\n                width: calc(95% / 3);\r\n                posi